import { useState, useEffect } from 'react';
import axios from 'axios';

const StudentDashboard = () => {
  const [studentDetails, setStudentDetails] = useState({ email: '', phone: '' });
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [timer, setTimer] = useState(300); // Timer in seconds (5 minutes)

  // Fetch a random question from the backend
  const fetchRandomQuestion = () => {
    axios.get('http://localhost:5001/api/questions').then((response) => {
      const questions = response.data;
      if (questions.length > 0) {
        const randomIndex = Math.floor(Math.random() * questions.length);
        setQuestion(questions[randomIndex]);
      } else {
        setQuestion('No questions available. Please ask your teacher to add questions.');
      }
    });
  };

  // Handle form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!studentDetails.email || !studentDetails.phone) {
      alert('Please enter both email and phone number!');
      return;
    }
    setFormSubmitted(true);
    fetchRandomQuestion(); // Fetch question when form is submitted
  };

  // Handle timer countdown
  useEffect(() => {
    if (formSubmitted) {
      const interval = setInterval(() => {
        setTimer((prev) => (prev > 0 ? prev - 1 : 0));
      }, 1000);
      return () => clearInterval(interval); // Cleanup on unmount
    }
  }, [formSubmitted]);

  // Submit the answer
  const submitAnswer = () => {
    if (!answer.trim()) {
      alert('Answer cannot be empty!');
      return;
    }
    axios
      .post('http://localhost:5001/api/answers', {
        email: studentDetails.email,
        phone: studentDetails.phone,
        question,
        answer,
      })
      .then(() => {
        alert('Answer submitted successfully!');
        setAnswer('');
        fetchRandomQuestion(); // Load a new question
        setTimer(300); // Reset timer
      })
      .catch((error) => {
        alert('Failed to submit answer. Please try again.');
        console.error(error);
      });
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Student Dashboard</h1>
      {!formSubmitted ? (
        // Show input form
        <form className="space-y-4" onSubmit={handleFormSubmit}>
          <div>
            <label className="block mb-1 font-bold">Email:</label>
            <input
              type="email"
              className="border p-2 w-full"
              placeholder="Enter your email"
              value={studentDetails.email}
              onChange={(e) =>
                setStudentDetails({ ...studentDetails, email: e.target.value })
              }
            />
          </div>
          <div>
            <label className="block mb-1 font-bold">Phone:</label>
            <input
              type="tel"
              className="border p-2 w-full"
              placeholder="Enter your phone number"
              value={studentDetails.phone}
              onChange={(e) =>
                setStudentDetails({ ...studentDetails, phone: e.target.value })
              }
            />
          </div>
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Start
          </button>
        </form>
      ) : (
        // Show question, timer, and answer submission
        <div>
          <div className="mb-4">
            <p className="bg-gray-100 p-4 rounded">{question}</p>
          </div>
          <div className="mb-4">
            <p className="text-red-500 font-bold">
              Time Remaining: {Math.floor(timer / 60)}:{String(timer % 60).padStart(2, '0')}
            </p>
          </div>
          <textarea
            className="border p-2 w-full mb-4"
            rows="5"
            placeholder="Write your answer here..."
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
          />
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded"
            onClick={submitAnswer}
          >
            Submit Answer
          </button>
        </div>
      )}
    </div>
  );
};

export default StudentDashboard;
