import { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Performance = () => {
  const [performanceData, setPerformanceData] = useState([]);
  const [expandedIndex, setExpandedIndex] = useState(null);

  useEffect(() => {
    // Fetch mock performance data
    const mockData = [
      {
        name: 'John Doe',
        weeklyScores: [78, 85, 82, 88, 90],
        email: 'john@example.com',
        phone: '123-456-7890',
      },
      {
        name: 'Jane Smith',
        weeklyScores: [92, 95, 89, 93, 96],
        email: 'jane@example.com',
        phone: '987-654-3210',
      },
    ];
    setPerformanceData(mockData);
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Student Performance</h1>
      <div className="space-y-6">
        {performanceData.map((student, index) => (
          <div
            key={index}
            className="bg-white shadow-lg rounded-lg p-4 transition-all duration-300"
          >
            {/* Header */}
            <div
              className="flex justify-between items-center cursor-pointer"
              onClick={() =>
                setExpandedIndex(expandedIndex === index ? null : index)
              }
            >
              <div>
                <h2 className="text-lg font-bold">{student.name}</h2>
                <p className="text-sm text-gray-500">{student.email}</p>
              </div>
              <button className="text-blue-500">
                {expandedIndex === index ? 'Collapse' : 'Expand'}
              </button>
            </div>

            {/* Expandable Content */}
            {expandedIndex === index && (
              <div className="mt-4">
                {/* Line Chart */}
                <div className="mb-4">
                  <Line
                    data={{
                      labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4', 'Week 5'],
                      datasets: [
                        {
                          label: `${student.name}'s Weekly Scores`,
                          data: student.weeklyScores,
                          borderColor: 'rgba(75,192,192,1)',
                          backgroundColor: 'rgba(75,192,192,0.2)',
                          tension: 0.4,
                        },
                      ],
                    }}
                    options={{
                      responsive: true,
                      plugins: {
                        legend: { display: false },
                        title: { display: true, text: 'Weekly Performance' },
                      },
                    }}
                  />
                </div>

                {/* Additional Details */}
                <div className="text-sm text-gray-600">
                  <p>
                    <strong>Phone:</strong> {student.phone}
                  </p>
                  <p>
                    <strong>Email:</strong> {student.email}
                  </p>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Performance;
