// src/pages/Summary.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

const Summary = () => {
  const [enrolledStudents, setEnrolledStudents] = useState(0);
  const [totalSubmissions, setTotalSubmissions] = useState(0);
  const [remaining, setRemaining] = useState(0);
  // Removed: const [teacherName, setTeacherName] = useState("");

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        // Fetch total enrolled students
        const studentsResponse = await axios.get("http://localhost:5001/api/students");
        setEnrolledStudents(studentsResponse.data.length);

        // Fetch total submissions
        const submissionsResponse = await axios.get("http://localhost:5001/api/submissions");
        setTotalSubmissions(submissionsResponse.data.length);

        // Calculate remaining submissions
        setRemaining(studentsResponse.data.length - submissionsResponse.data.length);
      } catch (error) {
        console.error("Error fetching summary data:", error);
      }
    };

    fetchSummary();
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Summary</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white p-6 rounded shadow">
          <h2 className="text-xl font-semibold mb-2">Enrolled Students</h2>
          <p className="text-2xl">{enrolledStudents}</p>
        </div>
        <div className="bg-white p-6 rounded shadow">
          <h2 className="text-xl font-semibold mb-2">Total Submissions</h2>
          <p className="text-2xl">{totalSubmissions}</p>
        </div>
        <div className="bg-white p-6 rounded shadow">
          <h2 className="text-xl font-semibold mb-2">Remaining Submissions</h2>
          <p className="text-2xl">{remaining}</p>
        </div>
      </div>
    </div>
  );
};

export default Summary;
