import { Link, Outlet } from 'react-router-dom';
import {
  HomeIcon,
  ClipboardListIcon,
  BellIcon,
  CogIcon,
  AcademicCapIcon,
} from '@heroicons/react/outline';

const TeacherDashboardLayout = () => {
  const sidebarItems = [
    { name: 'Summary', icon: HomeIcon, path: '/teacher' },
    { name: 'Manage Questions', icon: ClipboardListIcon, path: '/teacher/questions' },
    { name: 'Performance', icon: AcademicCapIcon, path: '/teacher/performance' },
    { name: 'Notifications', icon: BellIcon, path: '/teacher/notifications' },
    { name: 'Settings', icon: CogIcon, path: '/teacher/settings' },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-white shadow-lg">
        <div className="p-4 flex items-center space-x-4">
          <div className="h-12 w-12 bg-blue-500 rounded-full"></div>
          <div>
            <p className="text-lg font-bold">Mr. Smith</p>
            <p className="text-sm text-gray-500">Teacher</p>
          </div>
        </div>
        <nav className="p-4">
          <ul>
            {sidebarItems.map((item, index) => (
              <li key={index} className="mb-2">
                <Link
                  to={item.path}
                  className="flex items-center space-x-4 p-2 rounded hover:bg-gray-100"
                >
                  <item.icon className="h-6 w-6 text-gray-500" />
                  <span className="text-gray-700">{item.name}</span>
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-6">
        <Outlet />
      </main>
    </div>
  );
};

export default TeacherDashboardLayout;
