import React, { useEffect, useState } from "react";
import axios from "axios";

const Performance = () => {
  const [submissions, setSubmissions] = useState([]);

  // Fetch submissions
  useEffect(() => {
    axios
      .get("http://localhost:5001/api/submissions")
      .then((response) => setSubmissions(response.data))
      .catch((error) => console.error("Failed to fetch submissions:", error));
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Student Submissions</h1>
      {submissions.length === 0 ? (
        <p>No submissions available.</p>
      ) : (
        <ul className="space-y-4">
          {submissions.map((submission) => (
            <li key={submission.id} className="bg-white p-4 rounded shadow">
              <h2 className="font-bold text-lg">{submission.email}</h2>
              <p>
                <strong>Question:</strong> {submission.question}
              </p>
              <p>
                <strong>Answer:</strong>{" "}
                <span
                  dangerouslySetInnerHTML={{
                    __html: submission.feedback.highlightedAnswer,
                  }}
                />
              </p>
              <p>
                <strong>Grade:</strong> {submission.feedback.rating}
              </p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Performance;
