const ManageQuestions = () => {
  const [questions, setQuestions] = useState([]);
  const [archivedQuestions, setArchivedQuestions] = useState([]);
  const [newQuestion, setNewQuestion] = useState({
    type: "essay", // default to essay
    question: "",
    category: "",
  });
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [deadline, setDeadline] = useState("");
  const [duration, setDuration] = useState(60); // default duration in minutes
  const [generatedLink, setGeneratedLink] = useState("");
  const [students, setStudents] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [showArchived, setShowArchived] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  // Fetch all active and archived questions
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/questions");
        setQuestions(response.data.filter((q) => !q.archived));
        setArchivedQuestions(response.data.filter((q) => q.archived));
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };

    // Fetch all students
    const fetchStudents = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/students");
        setStudents(response.data);
      } catch (error) {
        console.error("Error fetching students:", error);
      }
    };

    fetchQuestions();
    fetchStudents();
  }, []);

  // Handle input change for new question
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewQuestion((prev) => ({ ...prev, [name]: value }));
  };

  // Handle adding a new question
  const addQuestion = async () => {
    if (!newQuestion.question || !newQuestion.category) {
      setError("Please fill in all fields.");
      setSuccess("");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5001/api/questions", newQuestion);
      if (response.data.success) {
        setQuestions((prev) => [...prev, response.data.question]);
        setNewQuestion({ type: "essay", question: "", category: "" });
        setSuccess("Question added successfully.");
        setError("");
      } else {
        setError("Failed to add question.");
        setSuccess("");
      }
    } catch (error) {
      console.error("Error adding question:", error);
      setError("Error adding question.");
      setSuccess("");
    }
  };

  // Handle archiving a question
  const archiveQuestion = async (id) => {
    try {
      const response = await axios.patch(`http://localhost:5001/api/questions/${id}/archive`);
      if (response.data.success) {
        const updatedQuestion = response.data.question;
        setQuestions((prev) => prev.filter((q) => q._id !== id));
        setArchivedQuestions((prev) => [...prev, updatedQuestion]);
        setSuccess("Question archived successfully.");
        setError("");
      } else {
        setError("Failed to archive question.");
        setSuccess("");
      }
    } catch (error) {
      console.error("Error archiving question:", error);
      setError("Error archiving question.");
      setSuccess("");
    }
  };

  // Handle unarchiving a question
  const unarchiveQuestion = async (id) => {
    try {
      const response = await axios.patch(`http://localhost:5001/api/questions/${id}/archive`);
      if (response.data.success) {
        const updatedQuestion = response.data.question;
        setArchivedQuestions((prev) => prev.filter((q) => q._id !== id));
        setQuestions((prev) => [...prev, updatedQuestion]);
        setSuccess("Question unarchived successfully.");
        setError("");
      } else {
        setError("Failed to unarchive question.");
        setSuccess("");
      }
    } catch (error) {
      console.error("Error unarchiving question:", error);
      setError("Error unarchiving question.");
      setSuccess("");
    }
  };

  // Handle deleting a question
  const deleteQuestion = async (id) => {
    if (!window.confirm("Are you sure you want to delete this question? This action cannot be undone.")) return;

    try {
      const response = await axios.delete(`http://localhost:5001/api/questions/${id}`);
      if (response.data.success) {
        setQuestions((prev) => prev.filter((q) => q._id !== id));
        setArchivedQuestions((prev) => prev.filter((q) => q._id !== id));
        setSuccess("Question deleted successfully.");
        setError("");
      } else {
        setError("Failed to delete question.");
        setSuccess("");
      }
    } catch (error) {
      console.error("Error deleting question:", error);
      setError("Error deleting question.");
      setSuccess("");
    }
  };

  // Handle selecting/deselecting questions for exam link generation
  const handleSelectQuestion = (id) => {
    if (selectedQuestions.includes(id)) {
      setSelectedQuestions(selectedQuestions.filter((qid) => qid !== id));
    } else {
      setSelectedQuestions([...selectedQuestions, id]);
    }
  };

  // Handle generating exam link
  const generateExamLink = async () => {
    if (selectedQuestions.length === 0) {
      setError("Please select at least one question to generate an exam link.");
      setSuccess("");
      return;
    }

    if (!deadline) {
      setError("Please set a deadline for the exam.");
      setSuccess("");
      return;
    }

    // Ensure duration is a positive number
    if (duration <= 0) {
      setError("Duration must be a positive number.");
      setSuccess("");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5001/api/exams", {
        questionIds: selectedQuestions,
        deadline,
        duration,
      });

      if (response.data.success) {
        setGeneratedLink(response.data.exam.link);
        setSuccess("Exam link generated successfully.");
        setError("");
        setSelectedQuestions([]);
      } else {
        setError("Failed to generate exam link.");
        setSuccess("");
      }
    } catch (error) {
      console.error("Error generating exam link:", error);
      setError("Error generating exam link.");
      setSuccess("");
    }
  };

  // Handle search in archived questions
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // Filtered archived questions based on search term
  const filteredArchivedQuestions = archivedQuestions.filter((q) =>
    q.question.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="space-y-6">
      {/* Add New Question */}
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Add New Question</h3>
        {error && <p className="text-red-500 mb-2">{error}</p>}
        {success && <p className="text-green-500 mb-2">{success}</p>}
        <div className="space-y-4">
          <div>
            <label className="block text-gray-700">Type:</label>
            <select
              name="type"
              value={newQuestion.type}
              onChange={handleInputChange}
              className="w-full border rounded p-2 mt-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
            >
              <option value="essay">Essay</option>
              {/* Add other question types if needed */}
            </select>
          </div>
          <div>
            <label className="block text-gray-700">Question:</label>
            <textarea
              name="question"
              value={newQuestion.question}
              onChange={handleInputChange}
              className="w-full border rounded p-2 mt-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
              rows="3"
              placeholder="Enter the question here..."
            ></textarea>
          </div>
          <div>
            <label className="block text-gray-700">Category:</label>
            <input
              type="text"
              name="category"
              value={newQuestion.category}
              onChange={handleInputChange}
              className="w-full border rounded p-2 mt-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
              placeholder="e.g., Mathematics, Science"
              // Removed datalist or suggestions as per requirement
            />
          </div>
          <button
            onClick={addQuestion}
            className="bg-yellow-500 text-white px-4 py-2 rounded-full hover:bg-yellow-600 transition-colors duration-200 shadow"
          >
            Add Question
          </button>
        </div>
      </div>

      {/* Generate Exam Link */}
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Generate Exam Link</h3>
        {error && <p className="text-red-500 mb-2">{error}</p>}
        {success && <p className="text-green-500 mb-2">{success}</p>}
        <div className="space-y-4">
          <div>
            <label className="block text-gray-700">Select Questions:</label>
            <div className="max-h-40 overflow-y-auto border rounded p-2 mt-2">
              {questions.map((question) => (
                <div key={question._id} className="flex items-center mb-2">
                  <input
                    type="checkbox"
                    checked={selectedQuestions.includes(question._id)}
                    onChange={() => handleSelectQuestion(question._id)}
                    className="form-checkbox h-4 w-4 text-yellow-600"
                  />
                  <label className="ml-2 text-gray-700">{question.question}</label>
                </div>
              ))}
              {questions.length === 0 && <p className="text-gray-500">No active questions available.</p>}
            </div>
          </div>
          <div>
            <label className="block text-gray-700">Deadline:</label>
            <input
              type="datetime-local"
              value={deadline}
              onChange={(e) => setDeadline(e.target.value)}
              className="w-full border rounded p-2 mt-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
            />
          </div>
          <div>
            <label className="block text-gray-700">Duration (minutes):</label>
            <input
              type="number"
              value={duration}
              onChange={(e) => setDuration(parseInt(e.target.value))}
              className="w-full border rounded p-2 mt-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
              min="1"
            />
          </div>
          <button
            onClick={generateExamLink}
            className="bg-yellow-500 text-white px-4 py-2 rounded-full hover:bg-yellow-600 transition-colors duration-200 shadow"
          >
            Generate Exam Link
          </button>

          {generatedLink && (
            <div className="mt-4 p-4 bg-green-100 rounded-lg shadow">
              <h4 className="text-md font-semibold text-green-800">Generated Exam Link:</h4>
              <a
                href={generatedLink}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 underline break-all"
              >
                {generatedLink}
              </a>
            </div>
          )}
        </div>
      </div>

      {/* Manage Questions List */}
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold text-gray-700">Manage Questions</h3>
          <button
            onClick={() => setShowArchived(!showArchived)}
            className="bg-gray-500 text-white px-3 py-1 rounded-full hover:bg-gray-600 transition-colors duration-200 shadow"
          >
            {showArchived ? "Show Active" : "Show Archived"}
          </button>
        </div>
        {showArchived ? (
          <>
            <h4 className="text-md font-semibold text-gray-600 mb-2">Archived Questions</h4>
            {/* Search Bar for Archived Questions */}
            <div className="mb-4">
              <input
                type="text"
                value={searchTerm}
                onChange={handleSearch}
                placeholder="Search archived questions..."
                className="w-full border rounded p-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
              />
            </div>
            {filteredArchivedQuestions.length === 0 ? (
              <p className="text-gray-500">No archived questions match your search.</p>
            ) : (
              <div className="space-y-4">
                {filteredArchivedQuestions.map((question) => (
                  <div
                    key={question._id}
                    className="p-4 bg-gray-50 rounded-lg shadow flex justify-between items-center"
                  >
                    <div>
                      <p className="text-gray-800 font-medium">{question.question}</p>
                      <p className="text-gray-500 text-sm">{question.category}</p>
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => unarchiveQuestion(question._id)}
                        className="bg-yellow-500 text-white px-3 py-1 rounded-full hover:bg-yellow-600 transition-colors duration-200"
                        title="Unarchive"
                      >
                        Unarchive
                      </button>
                      <button
                        onClick={() => deleteQuestion(question._id)}
                        className="bg-red-500 text-white px-3 py-1 rounded-full hover:bg-red-600 transition-colors duration-200"
                        title="Delete"
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </>
        ) : (
          <>
            <h4 className="text-md font-semibold text-gray-600 mb-2">Active Questions</h4>
            {questions.length === 0 ? (
              <p className="text-gray-500">No active questions available.</p>
            ) : (
              <div className="space-y-4">
                {questions.map((question) => (
                  <div
                    key={question._id}
                    className="p-4 bg-gray-50 rounded-lg shadow flex justify-between items-center"
                  >
                    <div>
                      <p className="text-gray-800 font-medium">{question.question}</p>
                      <p className="text-gray-500 text-sm">{question.category}</p>
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => archiveQuestion(question._id)}
                        className="bg-yellow-500 text-white px-3 py-1 rounded-full hover:bg-yellow-600 transition-colors duration-200"
                        title="Archive"
                      >
                        Archive
                      </button>
                      <button
                        onClick={() => deleteQuestion(question._id)}
                        className="bg-red-500 text-white px-3 py-1 rounded-full hover:bg-red-600 transition-colors duration-200"
                        title="Delete"
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default ManageQuestions;
