import { useState, useEffect } from 'react';
import axios from 'axios';

const StudentDashboard = () => {
  const [studentDetails, setStudentDetails] = useState({ email: '', phone: '' });
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [timer, setTimer] = useState(300); // 5 minutes
  const [feedback, setFeedback] = useState(null); // Store NLP feedback

  const fetchRandomQuestion = () => {
    axios.get('http://localhost:5001/api/questions').then((response) => {
      const questions = response.data;
      if (questions.length > 0) {
        const randomIndex = Math.floor(Math.random() * questions.length);
        setQuestion(questions[randomIndex]);
      } else {
        setQuestion('No questions available. Please ask your teacher to add questions.');
      }
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!studentDetails.email || !studentDetails.phone) {
      alert('Please enter both email and phone number!');
      return;
    }
    setFormSubmitted(true);
    fetchRandomQuestion();
  };

  useEffect(() => {
    if (formSubmitted) {
      const interval = setInterval(() => {
        setTimer((prev) => (prev > 0 ? prev - 1 : 0));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [formSubmitted]);

  const submitAnswer = () => {
    if (!answer.trim()) {
      alert('Answer cannot be empty!');
      return;
    }

    axios
      .post('http://localhost:5001/api/answers', {
        email: studentDetails.email,
        question,
        answer,
      })
      .then((response) => {
        setFeedback(response.data.analysis);
        setAnswer('');
        fetchRandomQuestion();
        setTimer(300);
      })
      .catch((error) => {
        alert('Failed to submit answer. Please try again.');
        console.error(error);
      });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-200 via-blue-300 to-blue-500">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg">
        <h1 className="text-2xl font-bold text-center mb-6">
          {formSubmitted ? 'Essay Submission' : 'Student Dashboard'}
        </h1>
        {!formSubmitted ? (
          <form className="space-y-4" onSubmit={handleFormSubmit}>
            <div>
              <label className="block mb-1 font-bold">Email:</label>
              <input
                type="email"
                className="border p-2 w-full rounded"
                placeholder="Enter your email"
                value={studentDetails.email}
                onChange={(e) =>
                  setStudentDetails({ ...studentDetails, email: e.target.value })
                }
              />
            </div>
            <div>
              <label className="block mb-1 font-bold">Phone:</label>
              <input
                type="tel"
                className="border p-2 w-full rounded"
                placeholder="Enter your phone number"
                value={studentDetails.phone}
                onChange={(e) =>
                  setStudentDetails({ ...studentDetails, phone: e.target.value })
                }
              />
            </div>
            <button
              type="submit"
              className="bg-blue-500 text-white px-4 py-2 rounded w-full"
            >
              Start
            </button>
          </form>
        ) : (
          <div>
            <div className="mb-4">
              <p className="bg-gray-100 p-4 rounded">{question}</p>
            </div>
            <div className="mb-4">
              <p className="text-red-500 font-bold text-center">
                Time Remaining: {Math.floor(timer / 60)}:{String(timer % 60).padStart(2, '0')}
              </p>
            </div>
            <textarea
              className="border p-2 w-full mb-4 rounded"
              rows="5"
              placeholder="Write your answer here..."
              value={answer}
              onChange={(e) => setAnswer(e.target.value)}
            />
            <button
              className="bg-blue-500 text-white px-4 py-2 rounded w-full"
              onClick={submitAnswer}
            >
              Submit Answer
            </button>
            {feedback && (
              <div className="mt-4 bg-gray-100 p-4 rounded">
                <h3 className="text-lg font-bold">Feedback:</h3>
                <ul>
                  {feedback.errors.map((error, idx) => (
                    <li key={idx} className="text-red-500">
                      {error.type}: {error.message}
                    </li>
                  ))}
                </ul>
                <p className="mt-2 font-bold">Score: {feedback.score}%</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
