import React, { useState } from "react";
import { ArrowRightIcon, DownloadIcon } from "@heroicons/react/24/outline";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, LineElement, PointElement, Tooltip } from "chart.js";

// Register Chart.js elements
ChartJS.register(LineElement, PointElement, Tooltip);

const Performance = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [expandedStudent, setExpandedStudent] = useState(null);

  // Student data
  const students = [
    {
      id: "123456",
      name: "John Doe",
      email: "john@example.com",
      latestResult: "Gold",
      weeklyPerformance: [85, 90, 80, 95, 88],
      pastResults: [
        { week: "Week 4", score: 95 },
        { week: "Week 3", score: 80 },
        { week: "Week 2", score: 90 },
        { week: "Week 1", score: 85 },
      ],
    },
    {
      id: "654321",
      name: "Jane Smith",
      email: "jane@example.com",
      latestResult: "Silver",
      weeklyPerformance: [70, 75, 65, 80, 78],
      pastResults: [
        { week: "Week 4", score: 80 },
        { week: "Week 3", score: 65 },
        { week: "Week 2", score: 75 },
        { week: "Week 1", score: 70 },
      ],
    },
  ];

  const filteredStudents = students.filter(
    (student) =>
      student.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      student.id.includes(searchQuery)
  );

  const handleExportCSV = () => {
    const csvData = [
      ["Student ID", "Name", "Email", "Latest Result"],
      ...students.map((s) => [s.id, s.name, s.email, s.latestResult]),
    ];

    const csvContent =
      "data:text/csv;charset=utf-8," +
      csvData.map((e) => e.join(",")).join("\n");

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "student_performance.csv");
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const renderChart = (data) => {
    const chartData = {
      labels: ["Week 1", "Week 2", "Week 3", "Week 4", "Week 5"],
      datasets: [
        {
          label: "Weekly Performance",
          data,
          borderColor: "#F59E0B", // Yellow line
          borderWidth: 2,
          pointRadius: 4,
          pointBackgroundColor: "#F59E0B", // Yellow points
          pointHoverRadius: 6,
          tension: 0.3, // Smooth curve
        },
      ],
    };

    const chartOptions = {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        tooltip: {
          callbacks: {
            label: (tooltipItem) => `Score: ${tooltipItem.raw}%`, // Tooltip on hover
          },
        },
        legend: { display: false }, // Hide legend
      },
      scales: {
        x: { grid: { display: false } },
        y: { grid: { display: false }, ticks: { display: false } },
      },
    };

    return <Line data={chartData} options={chartOptions} />;
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <header className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-semibold text-gray-800">
          Student Performance
        </h1>
        <button
          onClick={handleExportCSV}
          className="bg-yellow-500 text-white px-4 py-2 rounded-md flex items-center space-x-2"
        >
          <DownloadIcon className="h-5 w-5" />
          <span>Export to CSV</span>
        </button>
      </header>

      {/* Search Bar */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search by student name or ID..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-full border rounded-md p-2"
        />
      </div>

      {/* Student List */}
      <ul>
        {filteredStudents.map((student) => (
          <li key={student.id} className="bg-white shadow-md rounded-lg p-4 mb-4">
            <div className="flex justify-between items-center">
              <div>
                <p className="text-lg font-semibold text-gray-800">
                  {student.name} ({student.id})
                </p>
                <p className="text-gray-600">{student.email}</p>
              </div>
              <div className="flex items-center space-x-2">
                <span
                  className={`px-2 py-1 rounded-full text-sm font-semibold ${
                    student.latestResult === "Gold"
                      ? "bg-yellow-100 text-yellow-600"
                      : student.latestResult === "Silver"
                      ? "bg-gray-200 text-gray-600"
                      : "bg-orange-100 text-orange-600"
                  }`}
                >
                  {student.latestResult}
                </span>
                <button
                  onClick={() =>
                    setExpandedStudent(
                      expandedStudent === student.id ? null : student.id
                    )
                  }
                  className="text-blue-600 flex items-center"
                >
                  {expandedStudent === student.id ? "Collapse" : "Expand"}
                  <ArrowRightIcon
                    className={`h-4 w-4 ml-1 transform ${
                      expandedStudent === student.id ? "rotate-90" : ""
                    }`}
                  />
                </button>
              </div>
            </div>
            {/* Expanded Details */}
            {expandedStudent === student.id && (
              <div className="mt-4">
                <div
                  className="bg-white shadow rounded-lg p-4"
                  style={{ height: "200px" }}
                >
                  {renderChart(student.weeklyPerformance)}
                </div>
                <h3 className="text-gray-800 font-semibold mt-4 mb-2">
                  Past Results
                </h3>
                <ul>
                  {student.pastResults.map((result, index) => (
                    <li
                      key={index}
                      className="flex justify-between items-center border-t py-2"
                    >
                      <span>{result.week}</span>
                      <span className="font-semibold">{result.score}%</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Performance;
