import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import TeacherDashboardLayout from './components/TeacherDashboardLayout';
import ManageQuestions from './pages/ManageQuestions';
import Performance from './pages/Performance';
import Notifications from './pages/Notifications';
import Summary from './pages/Summary'; // Import the Summary page

function App() {
  return (
    <Router>
      <Routes>
        {/* Teacher Dashboard Routes */}
        <Route path="/teacher" element={<TeacherDashboardLayout />}>
          <Route path="" element={<Summary />} /> {/* Summary Page */}
          <Route path="questions" element={<ManageQuestions />} /> {/* Manage Questions */}
          <Route path="performance" element={<Performance />} /> {/* Performance */}
          <Route path="notifications" element={<Notifications />} /> {/* Notifications */}
        </Route>

        {/* Default Route */}
        <Route
          path="/"
          element={
            <h1 className="text-2xl font-bold p-4">
              Welcome to the School Dashboard!
            </h1>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
