// src/pages/StudentDashboard.jsx

import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const StudentDashboard = () => {
  const { id } = useParams(); // Exam ID from the URL
  const [exam, setExam] = useState(null);
  const [loading, setLoading] = useState(true);
  const [answers, setAnswers] = useState({});
  const [submissionResult, setSubmissionResult] = useState(null);
  const [timer, setTimer] = useState(null);

  // Fetch exam details
  useEffect(() => {
    const fetchExam = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/exam/${id}`);
        if (response.data.success) {
          setExam(response.data.exam);
          setTimer(response.data.exam.duration * 60); // Set timer in seconds
        } else {
          alert("Exam not found.");
        }
      } catch (error) {
        console.error("Failed to fetch exam:", error);
        alert("Failed to fetch exam details.");
      } finally {
        setLoading(false);
      }
    };

    fetchExam();
  }, [id]);

  // Timer logic
  useEffect(() => {
    if (timer !== null && timer > 0) {
      const interval = setInterval(() => {
        setTimer((prev) => (prev > 0 ? prev - 1 : 0));
      }, 1000);

      // Automatically submit when timer reaches zero
      if (timer === 0) {
        handleSubmit();
      }

      return () => clearInterval(interval); // Cleanup interval
    }
  }, [timer]);

  // Handle answer input
  const handleAnswerChange = (questionId, value) => {
    setAnswers({ ...answers, [questionId]: value });
  };

  // Handle answer submission
  const handleSubmit = async () => {
    // Optional: Retrieve student's email from a secure source or session
    const studentEmail = "student@example.com"; // Replace with actual student's email

    // Validate that all questions have been answered
    const allAnswered = exam.questions.every(
      (q) => answers[q.id] && answers[q.id].trim() !== ""
    );

    if (!allAnswered) {
      alert("Please answer all questions before submitting.");
      return;
    }

    // Prepare answers array
    const submissionAnswers = exam.questions.map((q) => ({
      questionId: q.id,
      answer: answers[q.id],
      analysis: {}, // Placeholder: NLP analysis will be added by the backend
    }));

    try {
      const response = await axios.post("http://localhost:5001/api/submissions", {
        email: studentEmail,
        examId: id,
        answers: submissionAnswers,
      });

      if (response.data.success) {
        setSubmissionResult(response.data.submission);
        alert("Exam submitted successfully!");
      } else {
        alert("Failed to submit the exam.");
      }
    } catch (error) {
      console.error("Failed to submit exam:", error);
      alert("Error submitting the exam. Please try again.");
    }
  };

  if (loading) return <div>Loading exam details...</div>;

  if (!exam) return <div>Exam not found.</div>;

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-200 via-blue-300 to-blue-500">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg">
        <h1 className="text-2xl font-bold text-center mb-6">Exam</h1>

        {/* Timer */}
        <div className="mb-4">
          <p className="text-red-500 font-bold text-center">
            Time Remaining: {Math.floor(timer / 60)}:
            {String(timer % 60).padStart(2, "0")}
          </p>
        </div>

        {/* Questions */}
        {exam.questions.map((q) => (
          <div key={q.id} className="mb-4">
            <p className="bg-gray-100 p-4 rounded">{q.question}</p>
            <textarea
              className="border p-2 w-full mb-4 rounded"
              rows="3"
              placeholder="Type your answer..."
              value={answers[q.id] || ""}
              onChange={(e) => handleAnswerChange(q.id, e.target.value)}
            />
          </div>
        ))}

        {/* Submit Button */}
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded w-full"
          onClick={handleSubmit}
          disabled={timer === 0}
        >
          Submit Exam
        </button>

        {/* Submission Result */}
        {submissionResult && (
          <div className="mt-6 bg-yellow-100 p-4 rounded">
            <h3 className="font-semibold text-lg">Submission Result</h3>
            <p>
              <strong>Submission ID:</strong> {submissionResult._id}
            </p>
            <p>
              <strong>Submitted At:</strong>{" "}
              {new Date(submissionResult.submittedAt).toLocaleString()}
            </p>
            <p>
              <strong>Grade:</strong> {submissionResult.grade || "Pending"}
            </p>
            <p>
              <strong>Feedback:</strong> {submissionResult.feedback || "Pending"}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
