import { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import axios from 'axios';

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Performance = () => {
  const [performanceData, setPerformanceData] = useState([]);

  useEffect(() => {
    // Fetch performance data from the backend
    axios.get('http://localhost:5001/api/performance').then((response) => {
      setPerformanceData(response.data);
    });
  }, []);

  // Prepare data for the line chart
  const chartData = {
    labels: performanceData.map((student) => student.name),
    datasets: [
      {
        label: 'Average Score (%)',
        data: performanceData.map((student) => student.averageScore),
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        tension: 0.4,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Student Performance (Average Scores)',
      },
    },
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Student Performance</h1>
      <div className="bg-white shadow-lg rounded-lg p-6">
        <Line data={chartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default Performance;
