import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Summary = () => {
  const [data, setData] = useState({
    totalSubmitted: 0,
    totalWaiting: 0,
  });

  useEffect(() => {
    // Fetch summary data from the backend
    axios
      .get('http://localhost:5001/api/summary')
      .then((response) => setData(response.data))
      .catch((error) => console.error('API Error:', error));
  }, []);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-6">Welcome to Your Dashboard</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-lg font-bold mb-2">Total Submissions</h2>
          <p className="text-4xl font-bold text-blue-600">{data.totalSubmitted}</p>
        </div>
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-lg font-bold mb-2">Currently Waiting</h2>
          <p className="text-4xl font-bold text-red-600">{data.totalWaiting}</p>
        </div>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Link to="/teacher/questions">
          <div className="bg-blue-500 text-white rounded-lg shadow-lg p-4 text-center hover:bg-blue-600 transition-all duration-300">
            Manage Questions
          </div>
        </Link>
        <Link to="/teacher/performance">
          <div className="bg-green-500 text-white rounded-lg shadow-lg p-4 text-center hover:bg-green-600 transition-all duration-300">
            View Performance
          </div>
        </Link>
        <Link to="/teacher/notifications">
          <div className="bg-yellow-500 text-white rounded-lg shadow-lg p-4 text-center hover:bg-yellow-600 transition-all duration-300">
            Notifications
          </div>
        </Link>
      </div>
    </div>
  );
};

export default Summary;
