import React, { useState, useEffect } from "react";
import { Outlet, Link } from "react-router-dom";
import { io } from "socket.io-client";

const socket = io("https://your-backend.onrender.com", { transports: ["websocket"] });

const TeacherDashboardLayout = () => {
  const [notificationsCount, setNotificationsCount] = useState(0);

  useEffect(() => {
    socket.on("connect", () => {
      console.log("Socket connected with ID:", socket.id);
    });

    socket.on("newSubmission", (data) => {
      console.log("Layout received newSubmission event:", data);
      setNotificationsCount((prev) => prev + 1);
    });

    return () => {
      socket.off("connect");
      socket.off("newSubmission");
    };
  }, []);

  return (
    <div className="flex h-screen">
      <div className="w-64 bg-gray-800 text-white flex flex-col">
        <div className="p-6">
          <h2 className="text-2xl font-bold text-yellow-400">ESL English</h2>
        </div>
        <nav className="mt-4 flex-1 space-y-2">
          <Link
            to="/teacher"
            className="flex items-center px-6 py-2 hover:bg-gray-700"
          >
            Dashboard
          </Link>
          <Link
            to="/teacher/questions"
            className="flex items-center px-6 py-2 hover:bg-gray-700"
          >
            Manage Questions
          </Link>
          <Link
            to="/teacher/students"
            className="flex items-center px-6 py-2 hover:bg-gray-700"
          >
            Manage Students
          </Link>
          <Link
            to="/teacher/performance"
            className="flex items-center px-6 py-2 hover:bg-gray-700"
          >
            Performance
          </Link>
          <Link
            to="/teacher/notifications"
            className="relative flex items-center px-6 py-2 hover:bg-gray-700"
          >
            Notifications
            {notificationsCount > 0 && (
              <span className="absolute top-2 right-4 bg-red-500 text-white rounded-full px-2 text-xs">
                {notificationsCount}
              </span>
            )}
          </Link>
          <Link
            to="/teacher/settings"
            className="flex items-center px-6 py-2 hover:bg-gray-700"
          >
            Settings
          </Link>
        </nav>
      </div>

      <div className="flex-1 flex flex-col">
        <header className="flex justify-between items-center p-4 bg-white shadow">
          {/* top navbar logic if needed */}
        </header>
        <main className="flex-1 bg-gray-100 p-6 overflow-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default TeacherDashboardLayout;
