import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ExamPage = () => {
  const { id } = useParams(); // Extract exam ID from URL
  const [exam, setExam] = useState(null);
  const [answers, setAnswers] = useState({}); // Track answers for each question
  const [timer, setTimer] = useState(null); // Timer for the exam

  useEffect(() => {
    axios
      .get(`http://localhost:5001/exam/${id}`)
      .then((response) => {
        setExam(response.data.exam);
        setTimer(response.data.exam.duration * 60); // Convert duration to seconds
      })
      .catch((error) => {
        console.error("Error fetching exam:", error.response?.data || error.message);
        alert("Failed to load exam. Please try again.");
      });
  }, [id]);

  // Timer countdown
  useEffect(() => {
    if (timer > 0) {
      const interval = setInterval(() => setTimer((prev) => prev - 1), 1000);
      return () => clearInterval(interval);
    }
  }, [timer]);

  const handleSubmit = () => {
    axios
      .post("http://localhost:5001/api/submissions", {
        examId: id,
        answers,
      })
      .then(() => alert("Exam submitted successfully!"))
      .catch((error) => {
        console.error("Error submitting exam:", error.response?.data || error.message);
        alert("Failed to submit exam. Please try again.");
      });
  };

  if (!exam) return <p>Loading exam...</p>;

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">{`Exam - ${exam.id}`}</h1>
      <p>{`Time Remaining: ${Math.floor(timer / 60)}:${String(timer % 60).padStart(2, "0")}`}</p>
      {exam.questions.map((q) => (
        <div key={q.id} className="bg-white p-4 shadow rounded mb-4 w-full max-w-md">
          <p>{q.question}</p>
          <textarea
            className="w-full mt-2 p-2 border rounded"
            rows="4"
            value={answers[q.id] || ""}
            onChange={(e) => setAnswers({ ...answers, [q.id]: e.target.value })}
          />
        </div>
      ))}
      <button className="bg-blue-500 text-white px-4 py-2 rounded mt-4" onClick={handleSubmit}>
        Submit Exam
      </button>
    </div>
  );
};

export default ExamPage;
