// src/pages/ManageQuestions.jsx

import React, { useState } from "react";
import axios from "axios";

const ManageQuestions = () => {
  const [question, setQuestion] = useState("");
  const [category, setCategory] = useState("");
  const [activeQuestions, setActiveQuestions] = useState([]);
  const [archivedQuestions, setArchivedQuestions] = useState([]);
  const [generatedLink, setGeneratedLink] = useState("");
  const [deadline, setDeadline] = useState("");
  const [duration, setDuration] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [isLoading, setIsLoading] = useState(false); // Optional: For loading state

  // Add a question to the active list
  const handleAddQuestion = () => {
    if (!question.trim() || !category) {
      alert("Please enter a question and select a category.");
      return;
    }

    const newQuestion = { id: Date.now(), question, category };
    setActiveQuestions([...activeQuestions, newQuestion]);
    setQuestion("");
    setCategory("");
  };

  // Archive a question
  const handleArchiveQuestion = (id) => {
    const questionToArchive = activeQuestions.find((q) => q.id === id);
    if (!questionToArchive) return;

    setArchivedQuestions([...archivedQuestions, questionToArchive]);
    setActiveQuestions(activeQuestions.filter((q) => q.id !== id));
  };

  // Activate an archived question
  const handleActivateQuestion = (id) => {
    const questionToActivate = archivedQuestions.find((q) => q.id === id);
    if (!questionToActivate) return;

    setActiveQuestions([...activeQuestions, questionToActivate]);
    setArchivedQuestions(archivedQuestions.filter((q) => q.id !== id));
  };

  // Delete an active question
  const handleDeleteQuestion = (id) => {
    setActiveQuestions(activeQuestions.filter((q) => q.id !== id));
  };

  // Delete an archived question
  const handleDeleteArchivedQuestion = (id) => {
    setArchivedQuestions(archivedQuestions.filter((q) => q.id !== id));
  };

  // Generate a link for active questions using async/await
  const handleGenerateLink = async () => {
    if (!activeQuestions.length) {
      alert("No active questions to generate a link!");
      return;
    }

    if (!deadline || !duration) {
      alert("Please provide a deadline and duration for the exam.");
      return;
    }

    const payload = {
      questions: activeQuestions,
      deadline,
      duration,
    };

    console.log("Payload being sent:", payload);

    setIsLoading(true); // Start loading

    try {
      const response = await axios.post(
        "http://localhost:5001/api/questions/generate-link",
        payload
      );

      if (response.data.success) {
        const { link } = response.data.exam;
        setGeneratedLink(link); // Use the link directly from the backend
        alert("Link generated successfully!");
      } else {
        alert("Failed to generate link.");
      }
    } catch (error) {
      console.error("Error generating link:", error);
      alert("Failed to generate link. Please try again.");
    } finally {
      setIsLoading(false); // End loading
    }
  };

  // Filter archived questions by search query
  const filteredArchivedQuestions = archivedQuestions.filter((q) =>
    q.question.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-semibold text-gray-800 mb-6">
        Manage Questions
      </h1>

      {/* Add Question Section */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h2 className="text-lg font-semibold text-gray-800 mb-4">Add Question</h2>
        <div className="flex flex-wrap items-center space-x-4 mb-4">
          <input
            type="text"
            className="flex-grow border rounded-md p-2"
            placeholder="Enter a question..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          />
          <select
            className="border rounded-md p-2"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="">Select Category</option>
            <option value="General">General</option>
            <option value="Essay">Essay</option>
            <option value="Multiple Choice">Multiple Choice</option>
          </select>
          <button
            onClick={handleAddQuestion}
            className="bg-blue-500 text-white px-4 py-2 rounded-md"
          >
            Add Question
          </button>
        </div>
      </div>

      {/* Active Questions Section */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold text-gray-800">Active Questions</h2>
          <div className="flex space-x-4">
            <input
              type="datetime-local"
              className="border rounded-md p-2"
              placeholder="Deadline"
              value={deadline}
              onChange={(e) => setDeadline(e.target.value)}
            />
            <input
              type="number"
              className="border rounded-md p-2"
              placeholder="Duration (minutes)"
              value={duration}
              onChange={(e) => setDuration(e.target.value)}
            />
            <button
              onClick={handleGenerateLink}
              className={`bg-green-500 text-white px-4 py-2 rounded-md ${
                isLoading ? "opacity-50 cursor-not-allowed" : ""
              }`}
              disabled={isLoading}
            >
              {isLoading ? "Generating..." : "Generate Link"}
            </button>
          </div>
        </div>
        {activeQuestions.length > 0 ? (
          <ul>
            {activeQuestions.map((q) => (
              <li
                key={q.id}
                className="flex justify-between items-center border-t py-2"
              >
                <span>
                  <strong>{q.category}: </strong>
                  {q.question}
                </span>
                <div className="space-x-2">
                  <button
                    onClick={() => handleArchiveQuestion(q.id)}
                    className="bg-yellow-500 text-white px-2 py-1 rounded-md text-sm"
                  >
                    Archive
                  </button>
                  <button
                    onClick={() => handleDeleteQuestion(q.id)}
                    className="bg-red-500 text-white px-2 py-1 rounded-md text-sm"
                  >
                    Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No active questions available.</p>
        )}
        {generatedLink && (
          <div className="mt-4 p-4 bg-green-100 text-green-800 rounded-md">
            <p>Generated Link:</p>
            <a
              href={generatedLink}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 underline"
            >
              {generatedLink}
            </a>
          </div>
        )}
      </div>

      {/* Archived Questions Section */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold text-gray-800">Archived Questions</h2>
          <input
            type="text"
            className="border rounded-md p-2"
            placeholder="Search questions..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        {filteredArchivedQuestions.length > 0 ? (
          <ul>
            {filteredArchivedQuestions.map((q) => (
              <li
                key={q.id}
                className="flex justify-between items-center border-t py-2"
              >
                <span>
                  <strong>{q.category}: </strong>
                  {q.question}
                </span>
                <div className="space-x-2">
                  <button
                    onClick={() => handleActivateQuestion(q.id)}
                    className="bg-green-500 text-white px-2 py-1 rounded-md text-sm"
                  >
                    Activate
                  </button>
                  <button
                    onClick={() => handleDeleteArchivedQuestion(q.id)}
                    className="bg-red-500 text-white px-2 py-1 rounded-md text-sm"
                  >
                    Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No archived questions found.</p>
        )}
      </div>
    </div>
  );
};

export default ManageQuestions;
