import React from 'react';
import {
  AcademicCapIcon,
  BookOpenIcon,
  ClipboardDocumentListIcon as ClipboardListIcon,
} from '@heroicons/react/24/outline';

const Summary = () => {
  const summaryData = [
    {
      title: 'Total Students',
      count: 120,
      icon: <AcademicCapIcon className="h-12 w-12 text-white" />,
      gradient: 'from-blue-500 to-blue-700',
    },
    {
      title: 'Essays Submitted',
      count: 90,
      icon: <BookOpenIcon className="h-12 w-12 text-white" />,
      gradient: 'from-green-500 to-green-700',
    },
    {
      title: 'Pending Submissions',
      count: 30,
      icon: <ClipboardListIcon className="h-12 w-12 text-white" />,
      gradient: 'from-yellow-500 to-yellow-700',
    },
  ];

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
      {summaryData.map((item, index) => (
        <div
          key={index}
          className="p-6 bg-white rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300 flex items-center"
        >
          <div
            className={`p-4 rounded-full bg-gradient-to-r ${item.gradient} flex items-center justify-center shadow-md`}
          >
            {item.icon}
          </div>
          <div className="ml-6">
            <h3 className="text-lg font-medium text-gray-600">{item.title}</h3>
            <p className="text-3xl font-extrabold text-gray-800">{item.count}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Summary;
