import React from "react";
import {
  CalendarDaysIcon,
  BellIcon,
  UserCircleIcon,
  ChartBarIcon,
  ClipboardDocumentListIcon,
  AcademicCapIcon,
} from "@heroicons/react/24/outline";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, LineElement, PointElement, Tooltip, Legend } from "chart.js";

// Register Chart.js elements
ChartJS.register(LineElement, PointElement, Tooltip, Legend);

const Summary = () => {
  // Sample Data
  const enrolledStudents = 120;
  const submissions = 90;
  const remaining = 30;
  const weeklyPerformance = [80, 70, 90, 75, 85]; // Weekly scores
  const studentList = [
    { name: "John Doe", performance: "Gold", score: 95 },
    { name: "Jane Smith", performance: "Silver", score: 85 },
    { name: "Mike Johnson", performance: "Bronze", score: 75 },
  ];

  // Chart.js Data and Options
  const chartData = {
    labels: ["Week 1", "Week 2", "Week 3", "Week 4", "Week 5"], // X-axis labels
    datasets: [
      {
        label: "Weekly Performance",
        data: weeklyPerformance,
        borderColor: "#4F46E5", // Purple line
        borderWidth: 2,
        pointRadius: 5,
        pointBackgroundColor: "#4F46E5", // Purple points
        pointHoverRadius: 8,
        tension: 0.3, // Smooth curve
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: (tooltipItem) => `Score: ${tooltipItem.raw}%`, // Custom tooltip label
        },
      },
      legend: { display: false }, // Hide the legend
    },
    scales: {
      x: {
        grid: { display: false }, // Remove X-axis grid lines
        ticks: { display: false }, // Hide X-axis labels
      },
      y: {
        grid: { display: false }, // Remove Y-axis grid lines
        ticks: { display: false }, // Hide Y-axis labels
      },
    },
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      {/* Top Section */}
      <header className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-semibold text-gray-800">Welcome, Teacher!</h1>
          <p className="text-gray-600">Your Dashboard Overview</p>
        </div>
        <div className="flex items-center space-x-4">
          {/* Notifications */}
          <button className="relative bg-white rounded-full p-2 shadow hover:shadow-lg">
            <BellIcon className="h-6 w-6 text-gray-600" />
            <span className="absolute top-0 right-0 bg-red-500 text-white text-xs font-bold rounded-full w-5 h-5 flex items-center justify-center">
              3
            </span>
          </button>
          {/* Calendar */}
          <button className="relative bg-white rounded-full p-2 shadow hover:shadow-lg">
            <CalendarDaysIcon className="h-6 w-6 text-gray-600" />
          </button>
          {/* Account */}
          <div className="relative bg-white rounded-full p-2 shadow hover:shadow-lg">
            <div className="h-8 w-8 flex items-center justify-center bg-blue-500 text-white rounded-full font-semibold">
              LM
            </div>
          </div>
        </div>
      </header>

      {/* Summary Cards */}
      <div className="grid grid-cols-3 gap-6 mb-8">
        <div className="bg-white shadow-md rounded-lg p-4 flex items-center space-x-4">
          <AcademicCapIcon className="h-12 w-12 text-blue-500" />
          <div>
            <h2 className="text-xl font-semibold text-gray-800">{enrolledStudents}</h2>
            <p className="text-gray-600">Enrolled Students</p>
          </div>
        </div>
        <div className="bg-white shadow-md rounded-lg p-4 flex items-center space-x-4">
          <ChartBarIcon className="h-12 w-12 text-green-500" />
          <div>
            <h2 className="text-xl font-semibold text-gray-800">{submissions}</h2>
            <p className="text-gray-600">Submissions</p>
          </div>
        </div>
        <div className="bg-white shadow-md rounded-lg p-4 flex items-center space-x-4">
          <ClipboardDocumentListIcon className="h-12 w-12 text-yellow-500" />
          <div>
            <h2 className="text-xl font-semibold text-gray-800">{remaining}</h2>
            <p className="text-gray-600">Remaining Submissions</p>
          </div>
        </div>
      </div>

      {/* Weekly Performance Chart */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h3 className="text-lg font-semibold text-gray-800 mb-4">Weekly Average Performance</h3>
        <Line data={chartData} options={chartOptions} />
      </div>

      {/* Continue Learning Section */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <h3 className="text-lg font-semibold text-gray-800 mb-4">Student Performance</h3>
        <div className="space-y-4">
          {studentList.map((student, index) => (
            <div key={index} className="flex justify-between items-center">
              <div>
                <h4 className="text-gray-800 font-medium">{student.name}</h4>
                <p className="text-gray-600 text-sm">Score: {student.score}%</p>
              </div>
              <span
                className={`text-sm px-2 py-1 rounded-full ${
                  student.performance === "Gold"
                    ? "bg-yellow-100 text-yellow-600"
                    : student.performance === "Silver"
                    ? "bg-gray-200 text-gray-600"
                    : "bg-orange-100 text-orange-600"
                }`}
              >
                {student.performance}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Summary;
