import React, { useState } from "react";
import {
  CalendarDaysIcon,
  BellIcon,
  UserCircleIcon,
  AcademicCapIcon,
  ChartBarIcon,
  ClipboardDocumentListIcon,
} from "@heroicons/react/24/outline";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  Tooltip,
  CategoryScale,
  LinearScale,
} from "chart.js";


// Register Chart.js components
ChartJS.register(LineElement, PointElement, Tooltip, CategoryScale, LinearScale);

const Summary = () => {
  const teacherName = "Lilac Marsh";
  const enrolledStudents = 120;
  const submissions = 90;
  const remaining = 30;

  const weeklyPerformance = [80, 70, 90, 75, 85];

  const [selectedWeek, setSelectedWeek] = useState("Week 1");
  const [searchQuery, setSearchQuery] = useState("");

  const studentList = [
    { id: "123456", name: "John Doe", performance: "Gold", score: 95 },
    { id: "654321", name: "Jane Smith", performance: "Silver", score: 85 },
    { id: "789012", name: "Mike Johnson", performance: "Bronze", score: 75 },
  ];

  const filteredStudents = studentList.filter((student) =>
    student.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const chartData = {
    labels: ["Day 1", "Day 2", "Day 3", "Day 4", "Day 5"],
    datasets: [
      {
        label: "Weekly Performance",
        data: weeklyPerformance,
        borderColor: "#F59E0B",
        borderWidth: 2,
        pointRadius: 4,
        pointBackgroundColor: "#F59E0B",
        pointHoverRadius: 6,
        tension: 0.3,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: (tooltipItem) => `Score: ${tooltipItem.raw}%`,
        },
      },
      legend: { display: false },
    },
    scales: {
      x: {
        grid: { display: false },
        ticks: { display: false },
      },
      y: {
        grid: { display: false },
        ticks: { display: false },
      },
    },
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      {/* Top Section */}
      <header className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-semibold text-gray-800">
            Welcome, {teacherName}!
          </h1>
          <p className="text-gray-600">
            Your Dashboard Overview - {selectedWeek}
          </p>
        </div>
        <div className="flex items-center space-x-4">
          {/* Notifications */}
          <button className="relative bg-white rounded-full p-2 shadow hover:shadow-lg">
            <BellIcon className="h-6 w-6 text-gray-600" />
            <span className="absolute top-0 right-0 bg-red-500 text-white text-xs font-bold rounded-full w-5 h-5 flex items-center justify-center">
              3
            </span>
          </button>
          {/* Calendar */}
          <button className="relative bg-white rounded-full p-2 shadow hover:shadow-lg">
            <CalendarDaysIcon className="h-6 w-6 text-gray-600" />
          </button>
          {/* Account */}
          <div className="relative bg-white rounded-full p-2 shadow hover:shadow-lg">
            <div className="h-8 w-8 flex items-center justify-center bg-yellow-500 text-white rounded-full font-semibold">
              LM
            </div>
          </div>
        </div>
      </header>

      {/* Summary Cards */}
      <div className="grid grid-cols-3 gap-6 mb-8">
        <div className="bg-white shadow-md rounded-lg p-4 flex items-center space-x-4">
          <AcademicCapIcon className="h-12 w-12 text-yellow-500" />
          <div>
            <h2 className="text-xl font-semibold text-gray-800">
              {enrolledStudents}
            </h2>
            <p className="text-gray-600">Enrolled Students</p>
          </div>
        </div>
        <div className="bg-white shadow-md rounded-lg p-4 flex items-center space-x-4">
          <ChartBarIcon className="h-12 w-12 text-yellow-500" />
          <div>
            <h2 className="text-xl font-semibold text-gray-800">
              {submissions}
            </h2>
            <p className="text-gray-600">Submissions</p>
          </div>
        </div>
        <div className="bg-white shadow-md rounded-lg p-4 flex items-center space-x-4">
          <ClipboardDocumentListIcon className="h-12 w-12 text-yellow-500" />
          <div>
            <h2 className="text-xl font-semibold text-gray-800">
              {remaining}
            </h2>
            <p className="text-gray-600">Remaining Submissions</p>
          </div>
        </div>
      </div>

      {/* Weekly Performance Chart */}
      <div
        className="bg-white shadow-md rounded-lg p-6 mb-8"
        style={{ height: "300px" }}
      >
        <h3 className="text-lg font-semibold text-gray-800 mb-4">
          Weekly Average Performance
        </h3>
        <div style={{ height: "220px" }}>
          <Line data={chartData} options={chartOptions} />
        </div>
      </div>

      {/* Student Performance Section */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold text-gray-800">
            Student Performance
          </h3>
          <div className="flex items-center space-x-2">
            <select
              className="border rounded-md p-2"
              value={selectedWeek}
              onChange={(e) => setSelectedWeek(e.target.value)}
            >
              <option value="Week 1">Week 1</option>
              <option value="Week 2">Week 2</option>
              <option value="Week 3">Week 3</option>
            </select>
            <input
              type="text"
              className="border rounded-md p-2"
              placeholder="Search students..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button className="bg-yellow-500 text-white px-4 py-2 rounded-md">
              Upload CSV
            </button>
          </div>
        </div>
        <table className="table-auto w-full">
          <thead>
            <tr className="text-left bg-gray-200">
              <th className="p-2">Student ID</th>
              <th className="p-2">Name</th>
              <th className="p-2">Score</th>
              <th className="p-2">Performance</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map((student, index) => (
              <tr key={index} className="border-t">
                <td className="p-2">{student.id}</td>
                <td className="p-2">{student.name}</td>
                <td className="p-2">{student.score}%</td>
                <td className="p-2">
                  <span
                    className={`px-2 py-1 rounded-full ${
                      student.performance === "Gold"
                        ? "bg-yellow-100 text-yellow-600"
                        : student.performance === "Silver"
                        ? "bg-gray-200 text-gray-600"
                        : "bg-orange-100 text-orange-600"
                    }`}
                  >
                    {student.performance}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Summary;
