// src/components/TeacherDashboardLayout.jsx

import React, { useEffect, useState } from "react";
import { Outlet, Link, useNavigate, useLocation } from "react-router-dom";
import {
  HomeIcon,
  ClipboardDocumentListIcon,
  UserGroupIcon,
  ChartBarIcon,
  BellIcon,
  Cog6ToothIcon,
  ArrowLeftOnRectangleIcon,
} from "@heroicons/react/24/outline";
import { io } from "socket.io-client";

// Initialize Socket.io client
const socket = io("http://localhost:5001"); // Ensure your backend is running on this URL

const TeacherDashboardLayout = () => {
  const [notifications, setNotifications] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Listen for 'newStudent' events from the backend
    socket.on("newStudent", (data) => {
      setNotifications((prev) => [...prev, `New student added: ${data.name}`]);
    });

    // Clean up the socket connection on component unmount
    return () => {
      socket.off("newStudent");
    };
  }, []);

  const handleLogout = () => {
    // Implement your logout logic here (e.g., clearing tokens, redirecting to login)
    // For example:
    // auth.logout();
    navigate("/"); // Redirect to login page
  };

  // Function to determine if the link is active
  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-64 bg-blue-800 text-white flex flex-col">
        <div className="p-6">
          <h2 className="text-2xl font-bold text-yellow-400">ESL English</h2> {/* ESL English in Yellow */}
        </div>
        <nav className="mt-10 flex-1">
          <Link
            to="/teacher"
            className={`flex items-center px-6 py-2 ${
              isActive("/teacher") ? "bg-blue-700" : "hover:bg-blue-700"
            } text-white`}
            title="Dashboard"
          >
            <HomeIcon className="h-6 w-6 mr-3" />
            Dashboard
          </Link>
          <Link
            to="/teacher/questions"
            className={`flex items-center px-6 py-2 ${
              isActive("/teacher/questions") ? "bg-blue-700" : "hover:bg-blue-700"
            } text-white`}
            title="Manage Questions"
          >
            <ClipboardDocumentListIcon className="h-6 w-6 mr-3" />
            Manage Questions
          </Link>
          <Link
            to="/teacher/students"
            className={`flex items-center px-6 py-2 ${
              isActive("/teacher/students") ? "bg-blue-700" : "hover:bg-blue-700"
            } text-white`}
            title="Manage Students"
          >
            <UserGroupIcon className="h-6 w-6 mr-3" />
            Manage Students
          </Link>
          <Link
            to="/teacher/performance"
            className={`flex items-center px-6 py-2 ${
              isActive("/teacher/performance") ? "bg-blue-700" : "hover:bg-blue-700"
            } text-white`}
            title="Performance"
          >
            <ChartBarIcon className="h-6 w-6 mr-3" />
            Performance
          </Link>
          <Link
            to="/teacher/notifications"
            className={`flex items-center px-6 py-2 ${
              isActive("/teacher/notifications") ? "bg-blue-700" : "hover:bg-blue-700"
            } text-white relative`}
            title="Notifications"
          >
            <BellIcon className="h-6 w-6 mr-3" />
            Notifications
            {notifications.length > 0 && (
              <span className="absolute top-2 right-6 bg-red-500 text-white rounded-full px-2 py-1 text-xs">
                {notifications.length}
              </span>
            )}
          </Link>
          <Link
            to="/teacher/settings"
            className={`flex items-center px-6 py-2 ${
              isActive("/teacher/settings") ? "bg-blue-700" : "hover:bg-blue-700"
            } text-white`}
            title="Settings"
          >
            <Cog6ToothIcon className="h-6 w-6 mr-3" />
            Settings
          </Link>
        </nav>
        <div className="p-6 border-t border-blue-700">
          <button
            onClick={handleLogout}
            className="flex items-center w-full text-white hover:bg-blue-700 px-4 py-2 rounded"
            title="Logout"
          >
            <ArrowLeftOnRectangleIcon className="h-6 w-6 mr-3" />
            Logout
          </button>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex-1 flex flex-col">
        {/* Top Navbar */}
        <header className="flex justify-between items-center p-4 bg-white shadow">
          <div className="flex items-center">
            {/* Teacher Icon */}
            <UserGroupIcon className="h-6 w-6 text-gray-700 mr-2" />
            <span className="font-semibold text-gray-700">Teacher Dashboard</span>
          </div>
          <div className="flex items-center space-x-4">
            {/* Bell Icon in Navbar */}
            <Link to="/teacher/notifications" className="relative" title="Notifications">
              <BellIcon className="h-6 w-6 text-gray-700" />
              {notifications.length > 0 && (
                <span className="absolute top-0 right-0 inline-flex items-center justify-center px-1 py-0.5 text-xs font-bold leading-none text-red-100 bg-red-600 rounded-full">
                  {notifications.length}
                </span>
              )}
            </Link>
            {/* Profile Dropdown */}
            <div className="relative">
              <button
                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                className="flex items-center focus:outline-none"
                title="User Menu"
              >
                {/* Profile Picture Placeholder */}
                <img
                  src="https://via.placeholder.com/32"
                  alt="Profile"
                  className="h-8 w-8 rounded-full"
                />
              </button>
              {isDropdownOpen && (
                <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg z-10">
                  <Link
                    to="/teacher/settings"
                    className="block px-4 py-2 text-gray-800 hover:bg-gray-100"
                    onClick={() => setIsDropdownOpen(false)}
                  >
                    Settings
                  </Link>
                  <button
                    onClick={() => {
                      handleLogout();
                      setIsDropdownOpen(false);
                    }}
                    className="w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-100"
                  >
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="flex-1 bg-gray-100 p-6 overflow-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default TeacherDashboardLayout;
