import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const StudentExamPage = () => {
  const { examId } = useParams();
  const navigate = useNavigate();
  const [exam, setExam] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(null);

  const studentEmail = sessionStorage.getItem('studentEmail');
  const studentStudentId = sessionStorage.getItem('studentId');

  useEffect(() => {
    if (!studentEmail || !studentStudentId) {
      navigate(`/student/login?examId=${examId}`);
      return;
    }

    const fetchExam = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/api/exams/${examId}`);
        if (response.data.success) {
          setExam(response.data.exam);
          setTimeLeft(response.data.exam.duration * 60);
        } else {
          setError('Exam not found.');
        }
      } catch (err) {
        console.error("Error fetching exam:", err);
        setError('Failed to load exam.');
      } finally {
        setLoading(false);
      }
    };

    fetchExam();
  }, [examId, navigate, studentEmail, studentStudentId]);

  useEffect(() => {
    let timer;
    if (timeLeft !== null && timeLeft > 0) {
      timer = setInterval(() => setTimeLeft((t) => t - 1), 1000);
    } else if (timeLeft === 0) {
      submitExam();
    }
    return () => clearInterval(timer);
  }, [timeLeft]);

  const formatTime = (seconds) => {
    const m = Math.floor(seconds / 60);
    const s = seconds % 60;
    return `${m}:${s < 10 ? '0' : ''}${s}`;
  };

  const handleAnswerChange = (questionId, value) => {
    setAnswers((prev) => ({ ...prev, [questionId]: value }));
  };

  const submitExam = async () => {
    if (!exam) return;

    const submissionAnswers = exam.questions.map((q) => ({
      questionId: q.id,
      answer: answers[q.id] || ""
    }));

    try {
      const response = await axios.post("http://localhost:5001/api/submissions", {
        email: studentEmail,
        examId: exam._id,
        answers: submissionAnswers
      });
      if (response.data.success) {
        alert("Exam submitted successfully.");
        navigate('/student/exams');
      } else {
        alert("Failed to submit exam.");
      }
    } catch (err) {
      console.error("Error submitting exam:", err);
      alert("Error submitting exam.");
    }
  };

  if (loading) return <div className="p-6 text-center">Loading exam...</div>;
  if (error) return <div className="p-6 text-red-500 text-center">{error}</div>;
  if (!exam) return null;

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden">
        {/* Header */}
        <div className="bg-yellow-500 text-white py-4 px-6">
          <h1 className="text-2xl font-bold">Exam</h1>
          <p className="mt-1">
            <strong>Deadline:</strong> {new Date(exam.deadline).toLocaleString()}
          </p>
          <p className="font-bold text-lg mt-1">
            Time Left: <span className="text-red-600">{formatTime(timeLeft)}</span>
          </p>
        </div>

        {/* Content */}
        <div className="p-6">
          <p className="text-gray-700 mb-4">
            <strong>Duration:</strong> {exam.duration} minutes
          </p>

          <div className="space-y-6">
            {exam.questions.map((q, index) => (
              <div key={q.id} className="bg-gray-50 p-4 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">
                  Q{index + 1}. {q.question}
                </h3>
                <textarea
                  className="w-full border rounded-md p-3 focus:outline-none focus:ring-2 focus:ring-yellow-500"
                  rows="4"
                  placeholder="Type your answer here..."
                  value={answers[q.id] || ""}
                  onChange={(e) => handleAnswerChange(q.id, e.target.value)}
                ></textarea>
              </div>
            ))}
          </div>

          <button
            onClick={submitExam}
            className={`mt-6 w-full py-3 rounded-md text-white font-semibold ${
              timeLeft === 0 ? 'bg-gray-400 cursor-not-allowed' : 'bg-yellow-500 hover:bg-yellow-600'
            } transition duration-200`}
            disabled={timeLeft === 0}
          >
            {timeLeft === 0 ? "Time's Up" : "Submit Exam"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default StudentExamPage;
