import React from "react";

const ExamPage = ({ questions, deadline, duration }) => {
  if (!questions || questions.length === 0) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <h1 className="text-2xl font-bold text-red-500">
          No questions available for this exam.
        </h1>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Exam</h1>
      <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
        <h2 className="text-lg font-semibold">Exam Details</h2>
        <p>
          <strong>Deadline:</strong> {new Date(deadline).toLocaleString()}
        </p>
        <p>
          <strong>Duration:</strong> {duration} minutes
        </p>
      </div>
      <div className="bg-white shadow-lg rounded-lg p-6">
        <h2 className="text-lg font-semibold mb-4">Questions</h2>
        <ul>
          {questions.map((q, index) => (
            <li key={q.id} className="mb-4">
              <p className="font-medium">
                Question {index + 1}: {q.question}
              </p>
              <textarea
                className="w-full border rounded p-2 mt-2"
                rows="4"
                placeholder="Write your answer here..."
              />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ExamPage;
