import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const StudentDashboard = () => {
  const { id } = useParams(); // Get the exam ID from the URL
  const navigate = useNavigate(); // For redirection if something goes wrong
  const [exam, setExam] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [answer, setAnswer] = useState("");
  const [timer, setTimer] = useState(null);

  // Fetch exam details
  useEffect(() => {
    if (!id) {
      setError("Exam ID is missing.");
      setLoading(false);
      return;
    }

    axios
      .get(`http://localhost:5001/exam/${id}`)
      .then((response) => {
        if (response.data.success) {
          setExam(response.data.exam);
          setTimer(response.data.exam.duration * 60); // Set timer in seconds
        } else {
          setError("Exam not found.");
        }
      })
      .catch((error) => {
        console.error("Failed to fetch exam:", error);
        setError("Failed to fetch exam details.");
      })
      .finally(() => setLoading(false));
  }, [id]);

  // Timer logic
  useEffect(() => {
    if (timer !== null && timer > 0) {
      const interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [timer]);

  const submitAnswer = () => {
    if (!answer.trim()) {
      alert("Answer cannot be empty!");
      return;
    }

    axios
      .post("http://localhost:5001/api/analyse-essay", { essay: answer })
      .then((response) => {
        alert("Answer submitted successfully!");
        setAnswer("");
      })
      .catch((error) => {
        console.error("Failed to submit answer:", error);
        alert("Error submitting answer.");
      });
  };

  if (loading) {
    return <div>Loading exam details...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-200 via-blue-300 to-blue-500">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg">
        <h1 className="text-2xl font-bold text-center mb-6">Exam</h1>

        {/* Display Questions */}
        {exam.questions.map((q, index) => (
          <div key={q.id} className="mb-4">
            <p className="bg-gray-100 p-4 rounded">{`${index + 1}. ${q.question}`}</p>
          </div>
        ))}

        {/* Timer */}
        <div className="mb-4">
          <p className="text-red-500 font-bold text-center">
            Time Remaining: {Math.floor(timer / 60)}:
            {String(timer % 60).padStart(2, "0")}
          </p>
        </div>

        {/* Answer Input */}
        <textarea
          className="border p-2 w-full mb-4 rounded"
          rows="5"
          placeholder="Write your answer here..."
          value={answer}
          onChange={(e) => setAnswer(e.target.value)}
        />

        {/* Submit Button */}
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded w-full"
          onClick={submitAnswer}
        >
          Submit Answer
        </button>
      </div>
    </div>
  );
};

export default StudentDashboard;
