// src/pages/Performance.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

const Performance = () => {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch submissions
  useEffect(() => {
    const fetchSubmissions = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/submissions");
        setSubmissions(response.data);
      } catch (error) {
        console.error("Failed to fetch submissions:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchSubmissions();
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Student Submissions</h1>
      {loading ? (
        <p>Loading submissions...</p>
      ) : submissions.length === 0 ? (
        <p>No submissions available.</p>
      ) : (
        <ul className="space-y-4">
          {submissions.map((submission) => (
            <li key={submission._id} className="bg-white p-4 rounded shadow">
              <h2 className="font-bold text-lg">{submission.email}</h2>
              <p>
                <strong>Submitted At:</strong>{" "}
                {new Date(submission.submittedAt).toLocaleString()}
              </p>
              <div className="mt-2">
                <strong>Answers:</strong>
                <ul className="list-disc list-inside">
                  {submission.answers.map((ans, idx) => (
                    <li key={idx} className="mt-1">
                      <p>
                        <strong>Question ID:</strong> {ans.questionId}
                      </p>
                      <p>
                        <strong>Answer:</strong>{" "}
                        <span
                          className="whitespace-pre-wrap"
                          dangerouslySetInnerHTML={{
                            __html: ans.answer, // Assuming answer is sanitized
                          }}
                        />
                      </p>
                      <p>
                        <strong>Feedback:</strong> {ans.analysis.feedback}
                      </p>
                      <p>
                        <strong>Score:</strong> {ans.analysis.score}%
                      </p>
                    </li>
                  ))}
                </ul>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Performance;
