import React, { useState } from "react";
import {
  AcademicCapIcon,
  ClipboardDocumentIcon,
  FolderArrowDownIcon,
} from "@heroicons/react/24/outline";

const ManageQuestions = () => {
  const [question, setQuestion] = useState("");
  const [category, setCategory] = useState("General");
  const [archivedQuestions, setArchivedQuestions] = useState([
    { id: 1, question: "What is the importance of education?", category: "Essay" },
    { id: 2, question: "Define democracy.", category: "Short Answer" },
  ]);
  const [activeQuestions, setActiveQuestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  const handleAddQuestion = () => {
    if (question.trim()) {
      setActiveQuestions([
        ...activeQuestions,
        { id: Date.now(), question, category },
      ]);
      setQuestion("");
    }
  };

  const handleArchiveQuestion = (id) => {
    const questionToArchive = activeQuestions.find((q) => q.id === id);
    if (questionToArchive) {
      setArchivedQuestions([...archivedQuestions, questionToArchive]);
      setActiveQuestions(activeQuestions.filter((q) => q.id !== id));
    }
  };

  const filteredArchivedQuestions = archivedQuestions.filter((q) =>
    q.question.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-semibold text-gray-800 mb-6">
        Manage Questions
      </h1>

      {/* Add Question Section */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h2 className="text-lg font-semibold text-gray-800 mb-4">Add Question</h2>
        <div className="flex space-x-4 mb-4">
          <input
            type="text"
            className="flex-grow border rounded-md p-2"
            placeholder="Enter a question..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          />
          <select
            className="border rounded-md p-2"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="General">General</option>
            <option value="Essay">Essay</option>
            <option value="Multiple Choice">Multiple Choice</option>
            <option value="Short Answer">Short Answer</option>
          </select>
          <button
            onClick={handleAddQuestion}
            className="bg-yellow-500 text-white px-4 py-2 rounded-md"
          >
            Add Question
          </button>
        </div>
        <div className="flex space-x-4">
          <input
            type="file"
            className="border rounded-md p-2"
            accept=".csv"
          />
          <button className="bg-yellow-500 text-white px-4 py-2 rounded-md">
            Upload CSV
          </button>
        </div>
      </div>

      {/* Active Questions Section */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h2 className="text-lg font-semibold text-gray-800 mb-4">
          Active Questions
        </h2>
        {activeQuestions.length > 0 ? (
          <ul>
            {activeQuestions.map((q) => (
              <li
                key={q.id}
                className="flex justify-between items-center border-t py-2"
              >
                <span>
                  <strong>{q.category}: </strong>
                  {q.question}
                </span>
                <button
                  onClick={() => handleArchiveQuestion(q.id)}
                  className="bg-yellow-500 text-white px-2 py-1 rounded-md text-sm"
                >
                  Archive
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No active questions available.</p>
        )}
      </div>

      {/* Archived Questions Section */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-lg font-semibold text-gray-800 mb-4">
          Archived Questions
        </h2>
        <div className="flex justify-between items-center mb-4">
          <input
            type="text"
            className="border rounded-md p-2 w-1/3"
            placeholder="Search archived questions..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        {filteredArchivedQuestions.length > 0 ? (
          <ul>
            {filteredArchivedQuestions.map((q) => (
              <li
                key={q.id}
                className="flex justify-between items-center border-t py-2"
              >
                <span>
                  <strong>{q.category}: </strong>
                  {q.question}
                </span>
                <button
                  onClick={() =>
                    setActiveQuestions([...activeQuestions, q])
                  }
                  className="bg-yellow-500 text-white px-2 py-1 rounded-md text-sm"
                >
                  Add Back
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No archived questions found.</p>
        )}
      </div>
    </div>
  );
};

export default ManageQuestions;
