import { Link, Outlet } from 'react-router-dom';

const TeacherDashboardLayout = () => {
  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-blue-800 text-white flex flex-col">
        <div className="p-4 text-lg font-bold">Teacher Dashboard</div>
        <nav className="flex-1">
          <ul>
            <li className="px-4 py-2 hover:bg-blue-700">
              <Link to="/teacher/questions">Manage Questions</Link>
            </li>
            <li className="px-4 py-2 hover:bg-blue-700">
              <Link to="/teacher/performance">Performance</Link>
            </li>
            <li className="px-4 py-2 hover:bg-blue-700">
              <Link to="/teacher/notifications">Notifications</Link>
            </li>
          </ul>
        </nav>
      </aside>
      {/* Main Content */}
      <main className="flex-1 p-6">
        <Outlet />
      </main>
    </div>
  );
};

export default TeacherDashboardLayout;
