import { useState, useEffect } from 'react';
import axios from 'axios';

const ManageQuestions = () => {
  const [questions, setQuestions] = useState([]);
  const [newQuestion, setNewQuestion] = useState('');
  const [category, setCategory] = useState('General');
  const [file, setFile] = useState(null); // State to hold the uploaded file

  useEffect(() => {
    axios.get('http://localhost:5001/api/questions').then((response) => {
      setQuestions(response.data);
    });
  }, []);

  const addQuestion = () => {
    if (!newQuestion.trim()) {
      alert('Question cannot be empty!');
      return;
    }
    axios
      .post('http://localhost:5001/api/questions', {
        question: newQuestion,
        category,
      })
      .then((response) => {
        setQuestions(response.data);
        setNewQuestion('');
      });
  };

  const handleFileUpload = async () => {
    if (!file) {
      alert('Please select a file!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5001/api/questions/batch', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setQuestions(response.data);
      alert('Questions uploaded successfully!');
    } catch (error) {
      console.error(error);
      alert('Failed to upload questions. Please try again.');
    }
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Manage Questions</h1>
      <div className="mb-6">
        <input
          type="text"
          placeholder="Enter a question"
          className="border p-2 w-full rounded mb-2"
          value={newQuestion}
          onChange={(e) => setNewQuestion(e.target.value)}
        />
        <select
          className="border p-2 rounded mb-2 w-full"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="General">General</option>
          <option value="Technology">Technology</option>
          <option value="Education">Education</option>
        </select>
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={addQuestion}
        >
          Add Question
        </button>
      </div>

      <div className="mb-6">
        <input
          type="file"
          accept=".csv"
          onChange={(e) => setFile(e.target.files[0])}
          className="border p-2 w-full rounded mb-2"
        />
        <button
          className="bg-green-500 text-white px-4 py-2 rounded"
          onClick={handleFileUpload}
        >
          Upload CSV
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {questions.map((q, index) => (
          <div
            key={index}
            className="bg-white shadow-lg rounded-lg p-4 flex justify-between items-center"
          >
            <div>
              <p className="font-bold">{q.category}</p>
              <p>{q.question}</p>
            </div>
            <button
              className="bg-red-500 text-white px-2 py-1 rounded"
              onClick={() => {
                axios.delete(`http://localhost:5001/api/questions/${index}`).then(() => {
                  setQuestions(questions.filter((_, i) => i !== index));
                });
              }}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageQuestions;
