import React from 'react';
import {
  AcademicCapIcon,
  ClipboardDocumentListIcon,
  ChartBarIcon,
  UserGroupIcon,
} from '@heroicons/react/24/outline';

const Summary = () => {
  const topCards = [
    {
      title: 'Enrolled Students',
      count: 120,
      icon: <UserGroupIcon className="h-8 w-8 text-blue-600" />,
      color: 'bg-blue-100',
    },
    {
      title: 'Essays Submitted',
      count: 90,
      icon: <ClipboardDocumentListIcon className="h-8 w-8 text-green-600" />,
      color: 'bg-green-100',
    },
    {
      title: 'Pending Submissions',
      count: 30,
      icon: <ChartBarIcon className="h-8 w-8 text-yellow-600" />,
      color: 'bg-yellow-100',
    },
  ];

  const progressData = [
    { week: 'Assessment Week 1', percentage: 80, color: 'bg-blue-500' },
    { week: 'Assessment Week 2', percentage: 60, color: 'bg-green-500' },
    { week: 'Assessment Week 3', percentage: 40, color: 'bg-yellow-500' },
  ];

  return (
    <div className="p-6 space-y-8">
      {/* Top Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {topCards.map((card, index) => (
          <div
            key={index}
            className={`p-6 rounded-lg shadow-md flex items-center ${card.color}`}
          >
            <div className="p-4 rounded-full bg-white shadow-md">{card.icon}</div>
            <div className="ml-4">
              <h3 className="text-lg font-medium text-gray-600">{card.title}</h3>
              <p className="text-3xl font-extrabold text-gray-800">{card.count}</p>
            </div>
            <button className="ml-auto bg-gray-200 text-sm px-4 py-1 rounded-lg hover:bg-gray-300">
              View Details
            </button>
          </div>
        ))}
      </div>

      {/* Weekly Progress */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">
          Weekly Progress
        </h2>
        <div className="space-y-4">
          {progressData.map((progress, index) => (
            <div key={index}>
              <div className="flex justify-between items-center mb-2">
                <span className="text-gray-700">{progress.week}</span>
                <span className="text-gray-600">{progress.percentage}%</span>
              </div>
              <div className="w-full bg-gray-200 h-4 rounded-full">
                <div
                  className={`h-4 rounded-full ${progress.color}`}
                  style={{ width: `${progress.percentage}%` }}
                ></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Summary;
