// src/pages/ManageQuestions.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

const ManageQuestions = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [deadline, setDeadline] = useState("");
  const [duration, setDuration] = useState(60);
  const [selectedStudents, setSelectedStudents] = useState([]);
  const [students, setStudents] = useState([]);
  const [examLink, setExamLink] = useState("");
  const [error, setError] = useState("");

  // Fetch all questions
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/questions");
        setQuestions(response.data);
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };

    fetchQuestions();
  }, []);

  // Fetch all students
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/students");
        setStudents(response.data);
      } catch (error) {
        console.error("Error fetching students:", error);
      }
    };

    fetchStudents();
  }, []);

  const handleQuestionSelect = (questionId) => {
    if (selectedQuestions.includes(questionId)) {
      setSelectedQuestions(selectedQuestions.filter((id) => id !== questionId));
    } else {
      setSelectedQuestions([...selectedQuestions, questionId]);
    }
  };

  const handleStudentSelect = (studentEmail) => {
    if (selectedStudents.includes(studentEmail)) {
      setSelectedStudents(selectedStudents.filter((email) => email !== studentEmail));
    } else {
      setSelectedStudents([...selectedStudents, studentEmail]);
    }
  };

  const generateExamLink = async () => {
    if (selectedQuestions.length === 0) {
      setError("Please select at least one question.");
      return;
    }

    if (!deadline) {
      setError("Please set a deadline.");
      return;
    }

    if (selectedStudents.length === 0) {
      setError("Please select at least one student.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5001/api/exams", {
        questionIds: selectedQuestions,
        deadline,
        duration,
        students: selectedStudents, // Pass selected students
      });

      if (response.data.success) {
        setExamLink(response.data.exam.link);
        setError("");
      } else {
        setError("Failed to generate exam link.");
      }
    } catch (error) {
      console.error("Error generating link:", error);
      setError("Error generating exam link.");
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Manage Questions</h1>

      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Select Questions for Exam</h2>
        <ul className="space-y-2">
          {questions.map((q) => (
            <li key={q._id} className="flex items-center">
              <input
                type="checkbox"
                id={q._id}
                checked={selectedQuestions.includes(q._id)}
                onChange={() => handleQuestionSelect(q._id)}
                className="mr-2"
              />
              <label htmlFor={q._id}>{q.question} ({q.category})</label>
            </li>
          ))}
        </ul>
      </div>

      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Select Students for Exam</h2>
        <ul className="space-y-2">
          {students.map((student) => (
            <li key={student._id} className="flex items-center">
              <input
                type="checkbox"
                id={student.email}
                checked={selectedStudents.includes(student.email)}
                onChange={() => handleStudentSelect(student.email)}
                className="mr-2"
              />
              <label htmlFor={student.email}>{student.name} ({student.email})</label>
            </li>
          ))}
        </ul>
      </div>

      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Exam Details</h2>
        <div className="space-y-4">
          <div>
            <label className="block text-gray-700">Deadline:</label>
            <input
              type="datetime-local"
              value={deadline}
              onChange={(e) => setDeadline(e.target.value)}
              className="w-full border rounded p-2"
            />
          </div>
          <div>
            <label className="block text-gray-700">Duration (minutes):</label>
            <input
              type="number"
              value={duration}
              onChange={(e) => setDuration(parseInt(e.target.value))}
              className="w-full border rounded p-2"
              min="1"
            />
          </div>
        </div>
      </div>

      {error && <p className="text-red-500 mb-4">{error}</p>}

      <button
        onClick={generateExamLink}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Generate Exam Link
      </button>

      {examLink && (
        <div className="mt-6 bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold">Generated Exam Link:</h2>
          <a href={examLink} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">
            {examLink}
          </a>
        </div>
      )}
    </div>
  );
};

export default ManageQuestions;
