import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

const Performance = () => {
  const [performanceData, setPerformanceData] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPerformanceData = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/performance");
        setPerformanceData(response.data);
      } catch (error) {
        console.error("Error fetching performance data:", error);
        setError("Failed to load performance data.");
      }
    };

    fetchPerformanceData();
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-semibold text-gray-800 mb-6">Performance Insights</h1>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      {performanceData.length === 0 ? (
        <p className="text-gray-600">No performance data available.</p>
      ) : (
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Student Performance Overview</h2>
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={performanceData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="studentName" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="averageGrade" fill="#f59e0b" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
};

export default Performance;
