import { useState, useEffect } from "react";
import axios from "axios";

const StudentDashboard = () => {
  const [studentInfo, setStudentInfo] = useState({ email: "", phone: "" });
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [timer, setTimer] = useState(300); // Timer in seconds (5 minutes)

  // Fetch a random question from the backend
  const fetchRandomQuestion = () => {
    axios.get("http://localhost:5001/api/questions").then((response) => {
      const questions = response.data;
      if (questions.length > 0) {
        const randomIndex = Math.floor(Math.random() * questions.length);
        setQuestion(questions[randomIndex]);
      } else {
        setQuestion(
          "No questions available. Please ask your teacher to add questions."
        );
      }
    });
  };

  // Handle timer countdown
  useEffect(() => {
    const interval = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);

    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  // Submit the answer
  const submitAnswer = () => {
    if (!answer.trim()) {
      alert("Answer cannot be empty!");
      return;
    }
    axios
      .post("http://localhost:5001/api/answers", {
        email: studentInfo.email, // Email set after login
        phone: studentInfo.phone, // Phone set after login
        question,
        answer,
      })
      .then(() => {
        alert("Answer submitted successfully!");
        setAnswer("");
        fetchRandomQuestion(); // Load a new question
        setTimer(300); // Reset timer
      })
      .catch((error) => {
        alert("Failed to submit answer. Please try again.");
        console.error(error);
      });
  };

  // Simulate fetching student info after login
  useEffect(() => {
    const studentData = {
      email: "lilac@student.esl.com", // Replace with dynamic data from login
      phone: "123-456-7890", // Replace with dynamic data from login
    };
    setStudentInfo(studentData);
    fetchRandomQuestion();
  }, []);

  return (
    <div
      className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-200 via-blue-300 to-blue-500"
    >
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg">
        <h1 className="text-2xl font-bold text-center mb-6">Essay Submission</h1>
        {/* Show question, timer, and answer submission */}
        <div>
          <div className="mb-4">
            <p className="bg-gray-100 p-4 rounded">{question}</p>
          </div>
          <div className="mb-4">
            <p className="text-red-500 font-bold text-center">
              Time Remaining: {Math.floor(timer / 60)}:
              {String(timer % 60).padStart(2, "0")}
            </p>
          </div>
          <textarea
            className="border p-2 w-full mb-4 rounded"
            rows="5"
            placeholder="Write your answer here..."
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
          />
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded w-full"
            onClick={submitAnswer}
          >
            Submit Answer
          </button>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
