import { useState, useEffect } from 'react';
import axios from 'axios';

const ManageQuestions = () => {
  const [questions, setQuestions] = useState([]);
  const [newQuestion, setNewQuestion] = useState('');

  // Fetch all questions from the backend
  useEffect(() => {
    axios.get('http://localhost:5001/api/questions').then((response) => {
      setQuestions(response.data);
    });
  }, []);

  // Add a new question
  const addQuestion = () => {
    if (!newQuestion.trim()) {
      alert('Question cannot be empty!');
      return;
    }
    axios
      .post('http://localhost:5001/api/questions', { question: newQuestion })
      .then((response) => {
        setQuestions(response.data); // Update questions list
        setNewQuestion(''); // Clear the input field
      })
      .catch((error) => {
        alert('Failed to add question. Please try again.');
        console.error(error);
      });
  };

  // Delete a question by index
  const deleteQuestion = (index) => {
    axios
      .delete(`http://localhost:5001/api/questions/${index}`)
      .then((response) => {
        setQuestions(response.data); // Update questions list
      })
      .catch((error) => {
        alert('Failed to delete question. Please try again.');
        console.error(error);
      });
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Manage Essay Questions</h1>
      <div className="mb-4">
        <input
          type="text"
          className="border p-2 w-full"
          placeholder="Enter a new essay question"
          value={newQuestion}
          onChange={(e) => setNewQuestion(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 mt-2 rounded"
          onClick={addQuestion}
        >
          Add Question
        </button>
      </div>
      <ul className="space-y-2">
        {questions.map((question, index) => (
          <li
            key={index}
            className="bg-gray-100 p-3 rounded flex justify-between items-center"
          >
            <span>{question}</span>
            <button
              className="bg-red-500 text-white px-2 py-1 rounded"
              onClick={() => deleteQuestion(index)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageQuestions;
