import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const StudentDashboard = () => {
  const { id } = useParams(); // Exam ID from the URL
  const [exam, setExam] = useState(null); // Exam details
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [feedback, setFeedback] = useState(null); // Analysis feedback

  // Fetch exam details by ID
  useEffect(() => {
    axios
      .get(`http://localhost:5001/exam/${id}`)
      .then((response) => setExam(response.data.exam))
      .catch((error) => console.error("Failed to load exam:", error));
  }, [id]);

  // Handle changes in answers
  const handleAnswerChange = (questionId, value) => {
    setAnswers({ ...answers, [questionId]: value });
  };

  // Submit answers for analysis
  const handleSubmit = () => {
    if (Object.values(answers).some((answer) => !answer.trim())) {
      alert("Please answer all questions before submitting!");
      return;
    }

    const submissions = exam.questions.map((q) => ({
      question: q.question,
      answer: answers[q.id] || "",
    }));

    axios
      .post("http://localhost:5001/api/analyse-answers", {
        email: "lilac@student.esl.com", // Replace with dynamic student email
        submissions,
      })
      .then((response) => {
        setFeedback(response.data.feedback);
        setSubmitted(true);
      })
      .catch((error) => console.error("Failed to submit answers:", error));
  };

  if (!exam) {
    return <div>Loading exam details...</div>;
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-200 via-blue-300 to-blue-500">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg">
        <h1 className="text-2xl font-bold text-center mb-6">
          {submitted ? "Feedback" : "Exam"}
        </h1>
        {!submitted ? (
          <div>
            {exam.questions.map((q) => (
              <div key={q.id} className="mb-4">
                <p className="font-semibold">{q.question}</p>
                <textarea
                  className="border p-2 w-full rounded"
                  rows="3"
                  placeholder="Write your answer here..."
                  onChange={(e) => handleAnswerChange(q.id, e.target.value)}
                />
              </div>
            ))}
            <button
              className="bg-blue-500 text-white px-4 py-2 rounded w-full"
              onClick={handleSubmit}
            >
              Submit Answers
            </button>
          </div>
        ) : (
          <div>
            <h2 className="text-lg font-semibold">Your Feedback</h2>
            {feedback.map((item, index) => (
              <div key={index} className="my-4">
                <p className="font-semibold">{item.question}</p>
                <p
                  className="bg-gray-100 p-2 rounded"
                  dangerouslySetInnerHTML={{ __html: item.highlightedAnswer }}
                />
                <p>
                  <strong>Score:</strong> {item.score}%
                </p>
                <p>
                  <strong>Rating:</strong> {item.rating}
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
