// src/pages/Performance.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

const Performance = () => {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch submissions
  useEffect(() => {
    const fetchSubmissions = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/submissions");
        setSubmissions(response.data);
      } catch (error) {
        console.error("Failed to fetch submissions:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchSubmissions();
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Student Submissions</h1>
      {loading ? (
        <p>Loading submissions...</p>
      ) : submissions.length === 0 ? (
        <p>No submissions available.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white">
            <thead>
              <tr>
                <th className="py-2 px-4 border-b">Student Email</th>
                <th className="py-2 px-4 border-b">Exam ID</th>
                <th className="py-2 px-4 border-b">Submitted At</th>
                <th className="py-2 px-4 border-b">Grade</th>
                <th className="py-2 px-4 border-b">Feedback</th>
              </tr>
            </thead>
            <tbody>
              {submissions.map((submission) => {
                const totalScore = submission.answers.reduce(
                  (acc, curr) => acc + (curr.analysis.score || 0),
                  0
                );
                const averageScore = (
                  totalScore / submission.answers.length
                ).toFixed(2);
                return (
                  <tr key={submission._id}>
                    <td className="py-2 px-4 border-b">{submission.email}</td>
                    <td className="py-2 px-4 border-b">{submission.examId}</td>
                    <td className="py-2 px-4 border-b">
                      {new Date(submission.submittedAt).toLocaleString()}
                    </td>
                    <td className="py-2 px-4 border-b">{averageScore}%</td>
                    <td className="py-2 px-4 border-b">
                      {submission.answers.map((ans, idx) => (
                        <div key={idx} className="mb-2">
                          <p className="font-semibold">
                            Question ID: {ans.questionId}
                          </p>
                          <p>
                            <strong>Feedback:</strong> {ans.analysis.feedback}
                          </p>
                          <p>
                            <strong>Score:</strong> {ans.analysis.score}%
                          </p>
                        </div>
                      ))}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Performance;
