import React from 'react';
import {
  AcademicCapIcon,
  BookOpenIcon,
  ClipboardDocumentListIcon as ClipboardListIcon,
} from '@heroicons/react/24/outline';

const Summary = () => {
  const summaryData = [
    {
      title: 'Total Students',
      count: 120,
      icon: <AcademicCapIcon className="h-8 w-8 text-blue-500" />,
      color: 'bg-blue-100',
    },
    {
      title: 'Essays Submitted',
      count: 90,
      icon: <BookOpenIcon className="h-8 w-8 text-green-500" />,
      color: 'bg-green-100',
    },
    {
      title: 'Pending Submissions',
      count: 30,
      icon: <ClipboardListIcon className="h-8 w-8 text-yellow-500" />,
      color: 'bg-yellow-100',
    },
  ];

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
      {summaryData.map((item, index) => (
        <div
          key={index}
          className={`p-6 rounded-lg shadow-lg flex items-center ${item.color}`}
        >
          <div className="p-4 bg-white rounded-full shadow-md">{item.icon}</div>
          <div className="ml-4">
            <h3 className="text-xl font-semibold">{item.title}</h3>
            <p className="text-2xl font-bold">{item.count}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Summary;
