// Inside TeacherDashboardLayout.jsx

import React, { useEffect, useState } from "react";
// ... other imports

const TeacherDashboardLayout = () => {
  // ... existing state and useEffect

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    // ... existing JSX
    <div className="flex items-center space-x-4">
      {/* Bell Icon in Navbar */}
      <Link to="/teacher/notifications" className="relative">
        <BellIcon className="h-6 w-6 text-gray-700" />
        {notifications.length > 0 && (
          <span className="absolute top-0 right-0 inline-flex items-center justify-center px-1 py-0.5 text-xs font-bold leading-none text-red-100 bg-red-600 rounded-full">
            {notifications.length}
          </span>
        )}
      </Link>
      {/* Profile Dropdown */}
      <div className="relative">
        <button
          onClick={toggleDropdown}
          className="flex items-center focus:outline-none"
        >
          {/* Profile Picture Placeholder */}
          <img
            src="https://via.placeholder.com/32"
            alt="Profile"
            className="h-8 w-8 rounded-full"
          />
        </button>
        {isDropdownOpen && (
          <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg">
            <Link
              to="/teacher/settings"
              className="block px-4 py-2 text-gray-800 hover:bg-gray-100"
              onClick={() => setIsDropdownOpen(false)}
            >
              Settings
            </Link>
            <button
              onClick={() => {
                handleLogout();
                setIsDropdownOpen(false);
              }}
              className="w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-100"
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
    // ... rest of JSX
  );
};
