// src/pages/ManageQuestions.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

const ManageQuestions = () => {
  const [questions, setQuestions] = useState([]);
  const [newQuestion, setNewQuestion] = useState({
    question: "",
    category: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Fetch all questions
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/questions");
        setQuestions(response.data);
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };

    fetchQuestions();
  }, []);

  // Handle input change for new question
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewQuestion((prev) => ({ ...prev, [name]: value }));
  };

  // Handle adding a new question
  const addQuestion = async () => {
    if (!newQuestion.question || !newQuestion.category) {
      setError("Please fill in all fields.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5001/api/questions", newQuestion);
      if (response.data.success) {
        setQuestions((prev) => [...prev, response.data.question]);
        setNewQuestion({ question: "", category: "" });
        setSuccess("Question added successfully.");
        setError("");
      } else {
        setError("Failed to add question.");
        setSuccess("");
      }
    } catch (error) {
      console.error("Error adding question:", error);
      setError("Error adding question.");
      setSuccess("");
    }
  };

  // Handle archiving a question
  const archiveQuestion = async (id) => {
    try {
      const response = await axios.patch(`http://localhost:5001/api/questions/${id}/archive`);
      if (response.data.success) {
        setQuestions((prev) =>
          prev.map((q) => (q._id === id ? { ...q, archived: true } : q))
        );
        setSuccess("Question archived successfully.");
        setError("");
      } else {
        setError("Failed to archive question.");
        setSuccess("");
      }
    } catch (error) {
      console.error("Error archiving question:", error);
      setError("Error archiving question.");
      setSuccess("");
    }
  };

  // Handle deleting a question
  const deleteQuestion = async (id) => {
    if (!window.confirm("Are you sure you want to delete this question?")) return;

    try {
      const response = await axios.delete(`http://localhost:5001/api/questions/${id}`);
      if (response.data.success) {
        setQuestions((prev) => prev.filter((q) => q._id !== id));
        setSuccess("Question deleted successfully.");
        setError("");
      } else {
        setError("Failed to delete question.");
        setSuccess("");
      }
    } catch (error) {
      console.error("Error deleting question:", error);
      setError("Error deleting question.");
      setSuccess("");
    }
  };

  return (
    <div className="space-y-6">
      {/* Add New Question */}
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Add New Question</h3>
        {error && <p className="text-red-500 mb-2">{error}</p>}
        {success && <p className="text-green-500 mb-2">{success}</p>}
        <div className="space-y-4">
          <div>
            <label className="block text-gray-700">Question:</label>
            <textarea
              name="question"
              value={newQuestion.question}
              onChange={handleInputChange}
              className="w-full border rounded p-2 mt-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
              rows="3"
              placeholder="Enter the question here..."
            ></textarea>
          </div>
          <div>
            <label className="block text-gray-700">Category:</label>
            <input
              type="text"
              name="category"
              value={newQuestion.category}
              onChange={handleInputChange}
              className="w-full border rounded p-2 mt-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
              placeholder="e.g., Mathematics, Science"
            />
          </div>
          <button
            onClick={addQuestion}
            className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
          >
            Add Question
          </button>
        </div>
      </div>

      {/* Questions List */}
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Manage Questions</h3>
        {questions.length === 0 ? (
          <p>No questions available.</p>
        ) : (
          <div className="space-y-4">
            {questions.map((question) => (
              <div
                key={question._id}
                className="p-4 bg-gray-50 rounded-lg shadow flex justify-between items-center"
              >
                <div>
                  <p className="text-gray-800 font-medium">{question.question}</p>
                  <p className="text-gray-500 text-sm">{question.category}</p>
                </div>
                <div className="flex space-x-2">
                  {!question.archived && (
                    <button
                      onClick={() => archiveQuestion(question._id)}
                      className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
                    >
                      Archive
                    </button>
                  )}
                  <button
                    onClick={() => deleteQuestion(question._id)}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ManageQuestions;
