// src/components/TeacherDashboardLayout.jsx

import React, { useEffect, useState } from "react";
import { Outlet, Link } from "react-router-dom";
import {
  HomeIcon,
  ClipboardDocumentListIcon, // Corrected Import
  UserGroupIcon,
  ChartBarIcon,
  BellIcon,
  Cog6ToothIcon,
} from "@heroicons/react/24/outline";
import { io } from "socket.io-client";

// Initialize Socket.io client
const socket = io("http://localhost:5001"); // Ensure your backend is running on this URL

const TeacherDashboardLayout = () => {
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    // Listen for 'newStudent' events from the backend
    socket.on("newStudent", (data) => {
      setNotifications((prev) => [...prev, `New student added: ${data.name}`]);
    });

    // Clean up the socket connection on component unmount
    return () => {
      socket.off("newStudent");
    };
  }, []);

  return (
    <div className="flex">
      {/* Sidebar */}
      <div className="w-64 bg-blue-800 text-white shadow-md min-h-screen">
        <div className="p-6">
          <h2 className="text-2xl font-bold text-yellow-400">ESL English</h2> {/* ESL English in Yellow */}
        </div>
        <nav className="mt-10">
          <Link
            to="/teacher"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <HomeIcon className="h-6 w-6 mr-3" />
            Dashboard
          </Link>
          <Link
            to="/teacher/questions"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <ClipboardDocumentListIcon className="h-6 w-6 mr-3" /> {/* Corrected Icon */}
            Manage Questions
          </Link>
          <Link
            to="/teacher/students"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <UserGroupIcon className="h-6 w-6 mr-3" />
            Manage Students
          </Link>
          <Link
            to="/teacher/performance"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <ChartBarIcon className="h-6 w-6 mr-3" />
            Performance
          </Link>
          <Link
            to="/teacher/notifications"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700 relative"
          >
            <BellIcon className="h-6 w-6 mr-3" />
            Notifications
            {notifications.length > 0 && (
              <span className="absolute top-2 right-6 bg-red-500 text-white rounded-full px-2 py-1 text-xs">
                {notifications.length}
              </span>
            )}
          </Link>
          <Link
            to="/teacher/settings"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <Cog6ToothIcon className="h-6 w-6 mr-3" />
            Settings
          </Link>
        </nav>
      </div>

      {/* Top Navbar */}
      <div className="flex-1 flex flex-col">
        <header className="flex justify-between items-center p-4 bg-white shadow">
          <div className="flex items-center">
            {/* Teacher Icon */}
            <UserGroupIcon className="h-6 w-6 text-gray-700 mr-2" />
            <span className="font-semibold text-gray-700">Teacher Dashboard</span>
          </div>
          <div className="flex items-center space-x-4">
            {/* Bell Icon in Navbar */}
            <Link to="/teacher/notifications" className="relative">
              <BellIcon className="h-6 w-6 text-gray-700" />
              {notifications.length > 0 && (
                <span className="absolute top-0 right-0 inline-flex items-center justify-center px-1 py-0.5 text-xs font-bold leading-none text-red-100 bg-red-600 rounded-full">
                  {notifications.length}
                </span>
              )}
            </Link>
            {/* Logout Button */}
            <button className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600">
              Logout
            </button>
          </div>
        </header>

        {/* Main Content */}
        <main className="flex-1 bg-gray-100 p-6">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default TeacherDashboardLayout;
