// frontend/src/pages/Notifications.jsx

import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    // Initialize Socket.io connection
    const newSocket = io("http://localhost:5001"); // Ensure this matches your backend URL
    setSocket(newSocket);

    // Listen for 'newStudent' events
    newSocket.on("newStudent", (data) => {
      setNotifications((prev) => [
        { type: "New Student", message: `${data.name} (${data.email}) has been added.` },
        ...prev,
      ]);
    });

    // Cleanup on component unmount
    return () => {
      newSocket.disconnect();
    };
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Notifications</h1>
      <div className="bg-white shadow-md rounded-lg p-4">
        {notifications.length === 0 ? (
          <p>No notifications available.</p>
        ) : (
          <ul>
            {notifications.map((notif, index) => (
              <li key={index} className="mb-2">
                <strong>{notif.type}:</strong> {notif.message}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Notifications;
