import { AcademicCapIcon, BookOpenIcon, ClipboardListIcon } from '@heroicons/react/24/outline';


const Summary = () => {
  const summaryData = [
    { icon: AcademicCapIcon, title: 'Enrolled Courses', count: 24, link: 'View details' },
    { icon: BookOpenIcon, title: 'Lessons', count: 56, link: 'View details' },
    { icon: ClipboardListIcon, title: 'Certificates', count: 17, link: 'View details' },
  ];

  const assessmentData = [
    { week: 'Week 1', progress: 70 },
    { week: 'Week 2', progress: 50 },
    { week: 'Week 3', progress: 85 },
  ];

  return (
    <div className="p-4">
      <h1 className="text-3xl font-bold mb-6">Welcome, Mr. Smith</h1>
      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {summaryData.map((item, index) => (
          <div key={index} className="bg-white rounded-lg shadow p-4 flex items-center space-x-4">
            <item.icon className="h-10 w-10 text-blue-500" />
            <div>
              <h2 className="text-lg font-semibold">{item.title}</h2>
              <p className="text-3xl font-bold text-blue-600">{item.count}</p>
              <a href="#" className="text-sm text-blue-500 underline">{item.link}</a>
            </div>
          </div>
        ))}
      </div>

      {/* Weekly Assessments */}
      <div className="mt-6">
        <h2 className="text-xl font-bold mb-4">Weekly Assessments</h2>
        <div className="bg-white rounded-lg shadow p-4">
          {assessmentData.map((data, index) => (
            <div key={index} className="mb-4">
              <p className="text-sm font-medium">{data.week}</p>
              <div className="bg-gray-200 rounded-full h-4 overflow-hidden">
                <div
                  className="bg-green-500 h-4 rounded-full"
                  style={{ width: `${data.progress}%` }}
                ></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Summary;
