import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ManageQuestions from './pages/ManageQuestions';
import StudentDashboard from './pages/StudentDashboard';
import Notifications from './pages/Notifications';
import Performance from './pages/Performance';

function App() {
  return (
    <Router>
      <div className="p-4">
        {/* Navigation */}
        <nav className="bg-gray-800 text-white p-4 rounded mb-4">
          <ul className="flex space-x-4">
            <li>
              <Link to="/teacher" className="hover:underline">
                Teacher Dashboard
              </Link>
            </li>
            <li>
              <Link to="/student" className="hover:underline">
                Student Dashboard
              </Link>
            </li>
          </ul>
        </nav>

        {/* Routes */}
        <Routes>
          {/* Teacher Dashboard */}
          <Route path="/teacher" element={<ManageQuestions />} />
          <Route path="/teacher/performance" element={<Performance />} />
          <Route path="/teacher/notifications" element={<Notifications />} />

          {/* Student Dashboard */}
          <Route path="/student" element={<StudentDashboard />} />

          {/* Default Route */}
          <Route
            path="/"
            element={
              <h1 className="text-2xl font-bold">
                Welcome to the School Dashboard!
              </h1>
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
