import { useState, useEffect } from 'react';
import axios from 'axios';

const Performance = () => {
  const [performanceData, setPerformanceData] = useState([]);

  useEffect(() => {
    // Fetch performance data from backend (mock API for now)
    axios.get('http://localhost:5001/api/performance').then((response) => {
      setPerformanceData(response.data);
    });
  }, []);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Student Performance</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {performanceData.map((student, index) => (
          <div
            key={index}
            className="bg-white p-4 shadow rounded-lg flex flex-col items-start"
          >
            <p className="font-bold">{student.name}</p>
            <p>Email: {student.email}</p>
            <p>Average Score: {student.averageScore}%</p>
            <p>Last Submission: {student.lastSubmission}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Performance;
