import { Link, Outlet, useLocation } from 'react-router-dom';
import { useState } from 'react';

const TeacherDashboardLayout = () => {
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // Navigation items with icons
  const navItems = [
    { name: 'Manage Questions', path: '/teacher/questions', icon: '‚ùì' },
    { name: 'Performance', path: '/teacher/performance', icon: 'üìà' },
    { name: 'Notifications', path: '/teacher/notifications', icon: 'üîî' },
  ];

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside
        className={`bg-blue-800 text-white flex flex-col ${
          sidebarOpen ? 'w-64' : 'w-16'
        } transition-all duration-300`}
      >
        {/* Sidebar Header */}
        <div className="p-4 text-lg font-bold flex justify-between items-center">
          {sidebarOpen && <span>Teacher Dashboard</span>}
          <button
            className="text-white focus:outline-none"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            {sidebarOpen ? '‚ùå' : '‚ò∞'}
          </button>
        </div>

        {/* Navigation Links */}
        <nav className="flex-1">
          <ul>
            {navItems.map((item, index) => (
              <li
                key={index}
                className={`px-4 py-2 hover:bg-blue-700 transition-all duration-300 ${
                  location.pathname === item.path ? 'bg-blue-700' : ''
                }`}
              >
                <Link
                  to={item.path}
                  className="flex items-center space-x-2"
                >
                  <span>{item.icon}</span>
                  {sidebarOpen && <span>{item.name}</span>}
                </Link>
              </li>
            ))}
          </ul>
        </nav>
      </aside>

      {/* Main Content Area */}
      <div className="flex-1">
        {/* Header */}
        <header className="bg-white shadow sticky top-0 z-10 p-4">
          <h1 className="text-lg font-bold">
            {navItems.find((item) => location.pathname.includes(item.path))?.name || 'Dashboard'}
          </h1>
        </header>

        {/* Page Content */}
        <main className="p-6">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default TeacherDashboardLayout;
