import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const StudentDashboard = () => {
  const { id } = useParams(); // Get the exam ID from the URL
  const navigate = useNavigate(); // Navigate after submission
  const [exam, setExam] = useState(null);
  const [loading, setLoading] = useState(true);
  const [answer, setAnswer] = useState("");
  const [analysisResult, setAnalysisResult] = useState(null);
  const [timer, setTimer] = useState(null);
  const [timeUp, setTimeUp] = useState(false); // Track if the timer is up
  const [isSubmitting, setIsSubmitting] = useState(false); // Prevent duplicate submissions

  // Fetch exam details
  useEffect(() => {
    axios
      .get(`http://localhost:5001/exam/${id}`)
      .then((response) => {
        if (response.data.success) {
          setExam(response.data.exam);
          setTimer(response.data.exam.duration * 60); // Set timer in seconds
        } else {
          alert("Failed to load exam. Please try again.");
          navigate("/student");
        }
      })
      .catch((error) => {
        console.error("Failed to fetch exam:", error);
        alert("Error loading exam. Please try again.");
        navigate("/student");
      })
      .finally(() => {
        setLoading(false);
      });
  }, [id, navigate]);

  // Timer logic
  useEffect(() => {
    if (timer !== null && timer > 0) {
      const interval = setInterval(() => {
        setTimer((prev) => prev - 1);
      }, 1000);
      return () => clearInterval(interval);
    } else if (timer === 0) {
      setTimeUp(true);
      alert("Time is up! Please submit your exam.");
    }
  }, [timer]);

  // Submit answer
  const submitAnswer = () => {
    if (!answer.trim()) {
      alert("Answer cannot be empty!");
      return;
    }
    if (timeUp) {
      alert("Time is up! You cannot submit the exam.");
      return;
    }
    setIsSubmitting(true);

    axios
      .post("http://localhost:5001/api/analyse-essay", { essay: answer })
      .then((response) => {
        setAnalysisResult(response.data.analysis);
        alert("Answer submitted and analysed successfully!");
        setAnswer("");
      })
      .catch((error) => {
        console.error("Failed to submit answer:", error);
        alert("Error analysing the answer.");
      })
      .finally(() => {
        setIsSubmitting(false);
      });
  };

  if (loading) {
    return <div>Loading exam details...</div>;
  }

  if (!exam) {
    return <div>Exam not found.</div>;
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-200 via-blue-300 to-blue-500">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg">
        <h1 className="text-2xl font-bold text-center mb-6">Exam</h1>

        {/* Questions */}
        {exam.questions.map((q, index) => (
          <div key={q.id} className="mb-4">
            <p className="bg-gray-100 p-4 rounded">{`${index + 1}. ${
              q.question
            }`}</p>
          </div>
        ))}

        {/* Timer */}
        <div className="mb-4">
          <p className="text-red-500 font-bold text-center">
            Time Remaining: {Math.floor(timer / 60)}:
            {String(timer % 60).padStart(2, "0")}
          </p>
        </div>

        {/* Answer Input */}
        <textarea
          className="border p-2 w-full mb-4 rounded"
          rows="5"
          placeholder="Write your answer here..."
          value={answer}
          onChange={(e) => setAnswer(e.target.value)}
          disabled={timeUp || isSubmitting}
        />

        {/* Submit Button */}
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded w-full disabled:opacity-50"
          onClick={submitAnswer}
          disabled={timeUp || isSubmitting}
        >
          Submit Answer
        </button>

        {/* Analysis Result */}
        {analysisResult && (
          <div className="mt-6 bg-yellow-100 p-4 rounded">
            <h3 className="font-semibold text-lg">Feedback</h3>
            <ul className="list-disc pl-6">
              {analysisResult.grammarMistakes.map((mistake, index) => (
                <li key={index}>
                  <strong>{mistake.mistake}</strong>: Corrected to "
                  {mistake.correction}" at position {mistake.position}
                </li>
              ))}
            </ul>
            <p className="mt-2">
              <strong>Overall Score:</strong> {analysisResult.overallScore}%
            </p>
            <p>
              <strong>Rating:</strong> {analysisResult.rating}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
