import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

const StudentLoginPage = () => {
  const [email, setEmail] = useState('');
  const [studentId, setStudentId] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5001/api/students/validate', { email, studentId });
      if (response.data.success) {
        // Store student info in sessionStorage
        sessionStorage.setItem('studentEmail', email);
        sessionStorage.setItem('studentId', studentId);

        // If there's an examId in query params, redirect there
        const params = new URLSearchParams(location.search);
        const examId = params.get('examId');
        if (examId) {
          navigate(`/student/exam/${examId}`);
        } else {
          // If no exam specified, go to a default student page (if you have one)
          // For now, just go to a placeholder page or show a message
          navigate('/student/exams'); // Make sure you have this route defined if needed.
        }
      } else {
        setError('Invalid student details.');
      }
    } catch (err) {
      console.error("Student login error:", err);
      setError('Failed to login.');
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-300 to-blue-500">
      <div className="bg-white p-8 rounded shadow-lg w-80">
        <h1 className="text-2xl font-bold mb-4 text-center">Student Login</h1>
        {error && <p className="text-red-500 mb-2">{error}</p>}
        <input
          type="text"
          placeholder="Enter your email"
          className="w-full border rounded p-2 mb-4"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter your student ID"
          className="w-full border rounded p-2 mb-4"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
        />
        <button
          onClick={handleLogin}
          className="w-full bg-yellow-500 text-white py-2 rounded hover:bg-yellow-600 transition-colors duration-200"
        >
          Login
        </button>
      </div>
    </div>
  );
};

export default StudentLoginPage;
