// src/components/TeacherDashboardLayout.jsx

import React, { useEffect, useState } from "react";
import { Outlet, Link } from "react-router-dom";
import {
  AcademicCapIcon,
  ChartBarIcon,
  ClipboardDocumentListIcon,
  BellIcon,
  Cog6ToothIcon,
} from "@heroicons/react/24/outline";
import { io } from "socket.io-client";

const socket = io("http://localhost:5001"); // Ensure backend is running on this URL

const TeacherDashboardLayout = () => {
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    socket.on("newStudent", (data) => {
      setNotifications((prev) => [...prev, `New student added: ${data.name}`]);
    });

    return () => {
      socket.off("newStudent");
    };
  }, []);

  return (
    <div className="flex">
      {/* Sidebar */}
      <div className="w-64 bg-blue-600 text-white shadow-md min-h-screen">
        <div className="p-6">
          <h2 className="text-2xl font-bold text-yellow-400">ESL English</h2> {/* Updated Title with Yellow */}
        </div>
        <nav className="mt-10">
          <Link
            to="/teacher"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <AcademicCapIcon className="h-6 w-6 mr-3" />
            Dashboard
          </Link>
          <Link
            to="/teacher/questions"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <ClipboardDocumentListIcon className="h-6 w-6 mr-3" />
            Manage Questions
          </Link>
          <Link
            to="/teacher/students"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <ClipboardDocumentListIcon className="h-6 w-6 mr-3" />
            Manage Students
          </Link>
          <Link
            to="/teacher/performance"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <ChartBarIcon className="h-6 w-6 mr-3" />
            Performance
          </Link>
          <Link
            to="/teacher/notifications"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <BellIcon className="h-6 w-6 mr-3" />
            Notifications
            {notifications.length > 0 && (
              <span className="ml-auto bg-red-500 text-white rounded-full px-2 py-1 text-xs">
                {notifications.length}
              </span>
            )}
          </Link>
          <Link
            to="/teacher/settings"
            className="flex items-center px-6 py-2 text-white hover:bg-blue-700"
          >
            <Cog6ToothIcon className="h-6 w-6 mr-3" />
            Settings
          </Link>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1">
        <Outlet />
      </div>
    </div>
  );
};

export default TeacherDashboardLayout;
