import { useState, useEffect } from 'react';
import axios from 'axios';

const Summary = () => {
  const [data, setData] = useState({
    totalSubmitted: 0,
    totalWaiting: 0,
  });

  useEffect(() => {
    // Fetch summary data from the backend
    axios
      .get('http://localhost:5001/api/summary')
      .then((response) => {
        console.log('API Response:', response.data); // Debugging: log the response data
        setData(response.data); // Update state with the fetched data
      })
      .catch((error) => {
        console.error('API Error:', error); // Debugging: log any errors
      });
  }, []);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-6">Dashboard Summary</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-lg font-bold">Total Submissions</h2>
          <p className="text-4xl font-bold text-blue-600">{data.totalSubmitted}</p>
        </div>
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-lg font-bold">Currently Waiting</h2>
          <p className="text-4xl font-bold text-red-600">{data.totalWaiting}</p>
        </div>
      </div>
    </div>
  );
};

export default Summary;
