import React, { useEffect, useState } from "react";
import axios from "axios";
import { Outlet, Link, useNavigate } from "react-router-dom";

const StudentDashboard = () => {
  const [submissions, setSubmissions] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSubmissions = async () => {
      const token = localStorage.getItem("studentToken");
      if (!token) {
        navigate("/student/login");
        return;
      }

      try {
        const response = await axios.get("http://localhost:5001/api/submissions", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (response.data.success) {
          setSubmissions(response.data.submissions);
        } else {
          setError(response.data.message || "Failed to load submissions.");
        }
      } catch (error) {
        console.error("Error fetching submissions:", error);
        setError("Error fetching submissions.");
      }
    };

    fetchSubmissions();
  }, [navigate]);

  return (
    <div className="space-y-6 p-6">
      <h2 className="text-2xl font-semibold text-gray-700">My Examinations</h2>
      {error && <p className="text-red-500">{error}</p>}
      {submissions.length === 0 ? (
        <p className="text-gray-600">You have not submitted any exams yet.</p>
      ) : (
        <div className="space-y-4">
          {submissions.map((submission) => (
            <div key={submission._id} className="bg-white p-6 rounded-lg shadow-lg">
              <h3 className="text-lg font-semibold text-gray-700 mb-2">
                Exam Link: {submission.examId.link}
              </h3>
              <p className="text-gray-600 mb-2">
                Submitted At: {new Date(submission.submittedAt).toLocaleString()}
              </p>
              <p className="text-gray-600 mb-4">
                Status:{" "}
                {submission.grades ? (
                  <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                    Completed
                  </span>
                ) : (
                  <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-yellow-100 text-yellow-800">
                    Pending
                  </span>
                )}
              </p>
              {submission.grades && (
                <div>
                  <h4 className="text-md font-semibold text-gray-600 mb-2">Grades:</h4>
                  <ul className="list-disc list-inside">
                    {Object.entries(submission.grades).map(([questionId, grade]) => (
                      <li key={questionId}>
                        Question ID: {questionId} - Grade: {grade}%
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default StudentDashboard;
