import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import { AcademicCapIcon, ClipboardListIcon } from "@heroicons/react/outline";

const Summary = () => {
  const [searchTerm, setSearchTerm] = useState("");

  // Example data for student performance
  const performanceData = [
    { id: "101", name: "John Doe", score: 95, rating: "Gold" },
    { id: "102", name: "Jane Smith", score: 85, rating: "Silver" },
    { id: "103", name: "Mike Johnson", score: 75, rating: "Bronze" },
  ];

  const filteredData = performanceData.filter((student) =>
    student.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const lineChartData = {
    labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
    datasets: [
      {
        data: [75, 85, 70, 90],
        borderColor: "#facc15", // Yellow for branding
        backgroundColor: "transparent",
        tension: 0.4,
        pointBackgroundColor: "#facc15",
        pointBorderColor: "#fff",
        pointBorderWidth: 2,
        pointRadius: 4,
      },
    ],
  };

  const lineChartOptions = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        callbacks: {
          label: (context) => `Score: ${context.raw}%`,
        },
      },
    },
    scales: {
      y: {
        display: false,
      },
      x: {
        grid: {
          display: false,
        },
        ticks: {
          font: {
            size: 12,
          },
        },
      },
    },
  };

  return (
    <div className="p-4 bg-gray-50 min-h-screen">
      {/* Welcome Header */}
      <div className="mb-6">
        <h1 className="text-2xl font-bold">Welcome, Lilac Marsh</h1>
        <p className="text-gray-500">Your Dashboard Overview - Week 3</p>
      </div>

      {/* Top Stats */}
      <div className="grid grid-cols-3 gap-4 mb-8">
        <div className="bg-yellow-100 p-4 rounded-lg shadow-md flex items-center">
          <AcademicCapIcon className="w-10 h-10 text-yellow-600" />
          <div className="ml-4">
            <p className="text-lg font-semibold">120</p>
            <p className="text-gray-600">Enrolled Students</p>
          </div>
        </div>
        <div className="bg-yellow-100 p-4 rounded-lg shadow-md flex items-center">
          <ClipboardListIcon className="w-10 h-10 text-yellow-600" />
          <div className="ml-4">
            <p className="text-lg font-semibold">90</p>
            <p className="text-gray-600">Submissions</p>
          </div>
        </div>
        <div className="bg-yellow-100 p-4 rounded-lg shadow-md flex items-center">
          <ClipboardListIcon className="w-10 h-10 text-yellow-600" />
          <div className="ml-4">
            <p className="text-lg font-semibold">30</p>
            <p className="text-gray-600">Remaining Submissions</p>
          </div>
        </div>
      </div>

      {/* Weekly Performance Graph */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-8">
        <h2 className="text-xl font-semibold mb-4">Weekly Average Performance</h2>
        <div className="w-full">
          <Line data={lineChartData} options={lineChartOptions} />
        </div>
      </div>

      {/* Student Performance */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Student Performance</h2>
          <input
            type="text"
            placeholder="Search by Name"
            className="border border-gray-300 rounded-lg p-2"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <table className="w-full text-left border-collapse">
          <thead>
            <tr className="border-b">
              <th className="py-2 px-4">Student ID</th>
              <th className="py-2 px-4">Name</th>
              <th className="py-2 px-4">Score</th>
              <th className="py-2 px-4">Rating</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.map((student) => (
              <tr key={student.id} className="border-b">
                <td className="py-2 px-4">{student.id}</td>
                <td className="py-2 px-4">{student.name}</td>
                <td className="py-2 px-4">{student.score}%</td>
                <td className="py-2 px-4">
                  <span
                    className={`px-3 py-1 rounded-full text-white ${
                      student.rating === "Gold"
                        ? "bg-yellow-500"
                        : student.rating === "Silver"
                        ? "bg-gray-400"
                        : "bg-orange-600"
                    }`}
                  >
                    {student.rating}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Summary;
