// src/pages/Home.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

const Home = () => {
  const [summary, setSummary] = useState({
    enrolledStudents: 0,
    totalSubmissions: 0,
    remaining: 0,
  });
  const [weeklyPerformance, setWeeklyPerformance] = useState([]);
  const [students, setStudents] = useState([]);

  useEffect(() => {
    // Fetch summary data
    const fetchSummary = async () => {
      try {
        const [studentsRes, submissionsRes] = await Promise.all([
          axios.get("http://localhost:5001/api/students"),
          axios.get("http://localhost:5001/api/submissions"),
        ]);

        const enrolledStudents = studentsRes.data.length;
        const totalSubmissions = submissionsRes.data.length;
        const remaining = enrolledStudents - totalSubmissions;

        setSummary({ enrolledStudents, totalSubmissions, remaining });
        setStudents(studentsRes.data);
      } catch (error) {
        console.error("Error fetching summary data:", error);
      }
    };

    // Fetch weekly performance data
    const fetchWeeklyPerformance = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/performance/weekly");
        setWeeklyPerformance(response.data); // Expected format: [{ week: 'Week 1', submissions: 10 }, ...]
      } catch (error) {
        console.error("Error fetching weekly performance:", error);
      }
    };

    fetchSummary();
    fetchWeeklyPerformance();
  }, []);

  return (
    <div className="space-y-6">
      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Enrolled Students */}
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <h3 className="text-lg font-semibold text-gray-700">Enrolled Students</h3>
          <p className="mt-4 text-3xl font-bold text-gray-900">{summary.enrolledStudents}</p>
        </div>
        {/* Total Submissions */}
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <h3 className="text-lg font-semibold text-gray-700">Total Submissions</h3>
          <p className="mt-4 text-3xl font-bold text-gray-900">{summary.totalSubmissions}</p>
        </div>
        {/* Remaining Submissions */}
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <h3 className="text-lg font-semibold text-gray-700">Remaining Submissions</h3>
          <p className="mt-4 text-3xl font-bold text-gray-900">{summary.remaining}</p>
        </div>
      </div>

      {/* Weekly Performance Line Chart */}
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Weekly Performance</h3>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={weeklyPerformance}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="week" />
            <YAxis />
            <Tooltip />
            <Line type="monotone" dataKey="submissions" stroke="#f59e0b" /> {/* Yellow Line */}
          </LineChart>
        </ResponsiveContainer>
      </div>

      {/* Students Table */}
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Students</h3>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th
                  scope="col"
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                >
                  Name
                </th>
                <th
                  scope="col"
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                >
                  ID
                </th>
                <th
                  scope="col"
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                >
                  Email
                </th>
                <th
                  scope="col"
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                >
                  Latest Grade
                </th>
                <th
                  scope="col"
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                >
                  Status
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {students.map((student) => (
                <tr key={student._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{student.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{student.id}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{student.email}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {student.latestGrade ? `${student.latestGrade}%` : "N/A"}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm">
                    {student.latestGrade ? (
                      <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                        Completed
                      </span>
                    ) : (
                      <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-yellow-100 text-yellow-800">
                        Pending
                      </span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Home;
