// frontend/src/components/TeacherDashboardLayout.jsx

import React, { useEffect, useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import {
  HomeIcon,
  ClipboardDocumentListIcon as ClipboardListIcon,
  BellIcon,
  CogIcon,
  AcademicCapIcon,
} from "@heroicons/react/24/outline";
import io from "socket.io-client";

const TeacherDashboardLayout = () => {
  const navigate = useNavigate();
  const [notificationCount, setNotificationCount] = useState(0);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    // Initialize Socket.io connection
    const newSocket = io("http://localhost:5001"); // Ensure this matches your backend URL
    setSocket(newSocket);

    // Listen for 'newStudent' events
    newSocket.on("newStudent", (data) => {
      setNotificationCount((prev) => prev + 1);
    });

    // Cleanup on component unmount
    return () => {
      newSocket.disconnect();
    };
  }, []);

  const handleSignOut = () => {
    const confirmSignOut = window.confirm("Are you sure you want to sign out?");
    if (confirmSignOut) {
      localStorage.removeItem("role");
      navigate("/");
    }
  };

  const menuItems = [
    { name: "Home", icon: <HomeIcon className="h-6 w-6" />, link: "/teacher" },
    {
      name: "Manage Questions",
      icon: <ClipboardListIcon className="h-6 w-6" />,
      link: "/teacher/questions",
    },
    {
      name: "Manage Students",
      icon: <AcademicCapIcon className="h-6 w-6" />,
      link: "/teacher/students",
    },
    {
      name: "Performance",
      icon: <AcademicCapIcon className="h-6 w-6" />,
      link: "/teacher/performance",
    },
    {
      name: "Notifications",
      icon: <BellIcon className="h-6 w-6" />,
      link: "/teacher/notifications",
    },
    {
      name: "Settings",
      icon: <CogIcon className="h-6 w-6" />,
      link: "/teacher/settings",
    },
  ];

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="bg-gray-800 text-white w-64 p-4">
        <h2 className="text-xl font-bold mb-6">Teacher Dashboard</h2>
        <ul className="space-y-4">
          {menuItems.map((item, index) => (
            <li key={index}>
              <Link
                to={item.link}
                className="flex items-center space-x-3 p-2 hover:bg-gray-700 rounded-lg"
              >
                {item.icon}
                <span>{item.name}</span>
              </Link>
            </li>
          ))}
        </ul>
      </aside>

      {/* Main Content */}
      <main className="flex-1 bg-gray-100 p-6 overflow-auto">
        {/* Top Bar */}
        <div className="flex justify-between items-center mb-6">
          {/* Left Side: Welcome Message */}
          <div>
            <h1 className="text-2xl font-semibold">Welcome, Lilac!</h1>
          </div>

          {/* Right Side: Notifications and Account */}
          <div className="flex items-center space-x-4">
            {/* Notifications */}
            <Link to="/teacher/notifications" className="relative">
              <BellIcon className="h-6 w-6 text-gray-600" />
              {notificationCount > 0 && (
                <span className="absolute top-0 right-0 bg-red-500 text-white text-xs font-bold rounded-full w-5 h-5 flex items-center justify-center">
                  {notificationCount}
                </span>
              )}
            </Link>

            {/* LM Icon for Account and Sign-Out */}
            <button
              onClick={handleSignOut}
              className="relative hover:bg-gray-700 p-1 rounded-full"
            >
              <div className="h-8 w-8 flex items-center justify-center bg-yellow-500 text-white rounded-full font-semibold">
                LM
              </div>
            </button>
          </div>
        </div>

        {/* Outlet for Nested Routes */}
        <Outlet />
      </main>
    </div>
  );
};

export default TeacherDashboardLayout;
